const express = require('express');
const { User, Field, CropSeason, Operation } = require('./models');
const bcrypt = require('bcrypt');
const jwt = require('jsonwebtoken');
const cors = require('cors');
require('dotenv').config();

const app = express();
const port = 3000;

app.use(express.json());
app.use(cors());

// Middleware to verify JWT token
const authenticateToken = (req, res, next) => {
    const token = req.headers['authorization'];
    if (!token) return res.status(401).json({ error: 'Unauthorized' });

    jwt.verify(token, process.env.JWT_SECRET, (err, user) => {
        if (err) return res.status(403).json({ error: 'Forbidden' });
        req.user = user;
        next();
    });
};

// Routes
app.post('/login', async (req, res) => {
    try {
        const { email, password } = req.body;
        const user = await User.findOne({ where: { email } });

        if (!user) return res.status(401).json({ error: 'Invalid credentials' });

        const passwordMatch = await bcrypt.compare(password, user.password_hash);

        if (!passwordMatch) return res.status(401).json({ error: 'Invalid credentials' });

        const token = jwt.sign({ id: user.id, role: user.role, farm_id: user.farm_id }, process.env.JWT_SECRET, { expiresIn: '1h' });
        res.json({ token });
    } catch (error) {
        console.error(error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

// Protected routes
app.get('/protected', authenticateToken, (req, res) => {
    res.json({ message: 'Protected route accessed', user: req.user });
});

// Field Routes
app.get('/fields', authenticateToken, async (req, res) => {
    try {
        const fields = await Field.findAll({ where: { farm_id: req.user.farm_id } });
        res.json(fields);
    } catch (error) {
        console.error(error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

// Add more routes for CRUD operations on fields, crop seasons, and operations

// Example Operation Route
app.post('/operations', authenticateToken, async (req, res) => {
    try {
        const { field_id, date, type, title, details } = req.body;

        // Validation
        if (date > new Date()) {
            return res.status(400).json({ error: 'Date cannot be in the future' });
        }

        const operation = await Operation.create({
            field_id,
            date,
            type,
            title,
            details,
            created_by: req.user.id
        });

        res.status(201).json(operation);
    } catch (error) {
        console.error(error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

app.listen(port, () => {
    console.log(`Server listening on port ${port}`);
});
