const { Sequelize, DataTypes } = require('sequelize');
const bcrypt = require('bcrypt');
const path = require('path');

// Configure Sequelize
const sequelize = new Sequelize(process.env.DB_NAME, process.env.DB_USER, process.env.DB_PASSWORD, {
    host: process.env.DB_HOST,
    port: process.env.DB_PORT,
    dialect: 'postgres'
});

// Define models
const Farm = sequelize.define('Farm', {
    name: {
        type: DataTypes.STRING,
        allowNull: false
    },
    address: {
        type: DataTypes.TEXT
    }
});

const User = sequelize.define('User', {
    email: {
        type: DataTypes.STRING,
        allowNull: false,
        unique: true
    },
    password_hash: {
        type: DataTypes.STRING,
        allowNull: false
    },
    role: {
        type: DataTypes.ENUM('admin', 'user'),
        defaultValue: 'user'
    }
}, {
    hooks: {
        beforeCreate: async (user) => {
            const salt = await bcrypt.genSalt(10);
            user.password_hash = await bcrypt.hash(user.password, salt);
        }
    }
});

const Field = sequelize.define('Field', {
    name: {
        type: DataTypes.STRING,
        allowNull: false
    },
    field_number: {
        type: DataTypes.STRING
    },
    area_ha: {
        type: DataTypes.DECIMAL(10, 2),
        allowNull: false,
        validate: {
            min: 0
        }
    },
    geometry: {
        type: DataTypes.JSONB
    },
    soil_type: {
        type: DataTypes.STRING
    },
    notes: {
        type: DataTypes.TEXT
    },
    active_from: {
        type: DataTypes.DATE,
        allowNull: false
    },
    active_to: {
        type: DataTypes.DATE
    }
});

const CropSeason = sequelize.define('CropSeason', {
    year: {
        type: DataTypes.INTEGER,
        allowNull: false,
        validate: {
            min: 1990,
            max: new Date().getFullYear() + 1
        }
    },
    crop_name: {
        type: DataTypes.STRING,
        allowNull: false
    },
    variety: {
        type: DataTypes.STRING
    },
    sowing_date: {
        type: DataTypes.DATE
    },
    harvest_date: {
        type: DataTypes.DATE
    },
    yield_t_ha: {
        type: DataTypes.DECIMAL(10, 2)
    }
}, {
    indexes: [
        {
            unique: true,
            fields: ['field_id', 'year']
        }
    ]
});

const Operation = sequelize.define('Operation', {
    date: {
        type: DataTypes.DATE,
        allowNull: false,
        validate: {
            isBefore: Date.now()
        }
    },
    type: {
        type: DataTypes.ENUM('tillage', 'sowing', 'fertilization', 'plant_protection', 'irrigation', 'harvest', 'other'),
        allowNull: false
    },
    title: {
        type: DataTypes.STRING,
        allowNull: false
    },
    details: {
        type: DataTypes.JSONB,
        allowNull: false
    }
});

// Define associations
Farm.hasMany(User, { foreignKey: 'farm_id', onDelete: 'CASCADE' });
User.belongsTo(Farm, { foreignKey: 'farm_id' });

Farm.hasMany(Field, { foreignKey: 'farm_id', onDelete: 'CASCADE' });
Field.belongsTo(Farm, { foreignKey: 'farm_id' });

Field.hasMany(CropSeason, { foreignKey: 'field_id', onDelete: 'CASCADE' });
CropSeason.belongsTo(Field, { foreignKey: 'field_id' });

Field.hasMany(Operation, { foreignKey: 'field_id', onDelete: 'CASCADE' });
Operation.belongsTo(Field, { foreignKey: 'field_id' });

CropSeason.hasMany(Operation, { foreignKey: 'crop_season_id', onDelete: 'SET NULL' });
Operation.belongsTo(CropSeason, { foreignKey: 'crop_season_id' });

User.hasMany(Operation, { foreignKey: 'created_by', onDelete: 'SET NULL' });
Operation.belongsTo(User, { foreignKey: 'created_by' });

module.exports = {
    Farm,
    User,
    Field,
    CropSeason,
    Operation,
    sequelize
};
```

