from flask import Flask, render_template, request, redirect, url_for
from flask_sqlalchemy import SQLAlchemy
from flask_wtf import FlaskForm
from wtforms import StringField, SelectField, TextAreaField, SubmitField
from wtforms.validators import DataRequired

app = Flask(__name__)
app.config['SECRET_KEY'] = 'secret_key'
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///todo.db'
db = SQLAlchemy(app)

class Todo(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    nr = db.Column(db.Integer, nullable=False)
    category = db.Column(db.String(100), nullable=False)
    priority = db.Column(db.String(100), nullable=False)
    status = db.Column(db.String(100), nullable=False)
    name = db.Column(db.String(100), nullable=False)
    description = db.Column(db.Text, nullable=False)

    def __repr__(self):
        return f'Todo({self.name}, {self.status})'

class TodoForm(FlaskForm):
    nr = StringField('NR', validators=[DataRequired()])
    category = SelectField('Kategorie', choices=['Bug', 'Idee', 'Aufgabe'], validators=[DataRequired()])
    priority = SelectField('Priorität', choices=['Hoch', 'Mittel', 'Niedrig'], validators=[DataRequired()])
    status = SelectField('Status', choices=['Offen', 'In Bearbeitung', 'Erledigt'], validators=[DataRequired()])
    name = StringField('Name', validators=[DataRequired()])
    description = TextAreaField('Beschreibung', validators=[DataRequired()])
    submit = SubmitField('Speichern')

@app.route('/')
def index():
    todos = Todo.query.all()
    return render_template('index.html', todos=todos)

@app.route('/create', methods=['GET', 'POST'])
def create():
    form = TodoForm()
    if form.validate_on_submit():
        todo = Todo(nr=form.nr.data, category=form.category.data, priority=form.priority.data, status=form.status.data, name=form.name.data, description=form.description.data)
        db.session.add(todo)
        db.session.commit()
        return redirect(url_for('index'))
    return render_template('create.html', form=form)

@app.route('/update/<int:id>', methods=['GET', 'POST'])
def update(id):
    todo = Todo.query.get_or_404(id)
    form = TodoForm()
    if form.validate_on_submit():
        todo.nr = form.nr.data
        todo.category = form.category.data
        todo.priority = form.priority.data
        todo.status = form.status.data
        todo.name = form.name.data
        todo.description = form.description.data
        db.session.commit()
        return redirect(url_for('index'))
    elif request.method == 'GET':
        form.nr.data = todo.nr
        form.category.data = todo.category
        form.priority.data = todo.priority
        form.status.data = todo.status
        form.name.data = todo.name
        form.description.data = todo.description
    return render_template('update.html', form=form)

@app.route('/delete/<int:id>')
