    item_type = SelectField('Item Type', choices=[('bug', 'Bug'), ('idea', 'Idea'), ('todo', 'Todo')], validators=[InputRequired()])
    nr = IntegerField('NR', validators=[InputRequired(), NumberRange(min=1)])
    name = StringField('Name', validators=[InputRequired()])
    description = TextAreaField('Description')
    status = SelectField('Status', choices=[('open', 'Open'), ('in progress', 'In Progress'), ('resolved', 'Resolved'), ('closed', 'Closed')], validators=[InputRequired()])
    priority = SelectField('Priority', choices=[('low', 'Low'), ('medium', 'Medium'), ('high', 'High')])
    assigned_to = StringField('Assigned To')
    due_date = StringField('Due Date') # Input as string, handled during form submission

@app.route('/')
def index():
    items = Item.query.all()
    return render_template('index.html', items=items)

@app.route('/create', methods=['GET', 'POST'])
def create():
    form = ItemForm()
    if form.validate_on_submit():
        try:
            # Convert due_date string to datetime object if provided
            due_date = form.due_date.data if form.due_date.data else None
            if due_date:
                due_date = datetime.strptime(due_date, '%Y-%m-%d')

            item = Item(item_type=form.item_type.data, nr=form.nr.data, name=form.name.data,
                        description=form.description.data, status=form.status.data,
                        priority=form.priority.data, assigned_to=form.assigned_to.data, due_date=due_date)
            db.session.add(item)
            db.session.commit()
            flash('Item created successfully!', 'success')
            return redirect(url_for('index'))
        except ValueError as e:
            flash(f'Invalid date format. Please use YYYY-MM-DD. Error: {e}', 'error')
            logging.error(f"Date format error: {e}")
            return render_template('create.html', form=form)  # Re-render the form with the error

    return render_template('create.html', form=form)

@app.route('/update/<int:id>', methods=['GET', 'POST'])
def update(id):
    item = Item.query.get_or_404(id)
    form = ItemForm()
    form.name.data = item.name
    form.description.data = item.description
    form.status.data = item.status
    form.priority.data = item.priority
    form.assigned_to.data = item.assigned_to

    if form.validate_on_submit():
        try:
            # Convert due_date string to datetime object if provided
