app.config['SECRET_KEY'] = os.environ.get('SECRET_KEY', 'your_default_secret_key')  # Load from environment
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///todo.db'
app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = False
bootstrap = BootstrapFlask(app)
csrf = CSRFProtect(app)

db = SQLAlchemy(app)

# Define the database models
class Task(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    task_number = db.Column(db.Integer, unique=True, nullable=False)
    name = db.Column(db.String(200), nullable=False)
    description = db.Column(db.Text)
    status = db.Column(db.String(50), default='Open', nullable=False)
    created_at = db.Column(db.DateTime, default=datetime.utcnow)
    updated_at = db.Column(db.DateTime, default=datetime.utcnow, onupdate=datetime.utcnow)

    def __repr__(self):
        return f'<Task {self.name}>'

class Bug(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    bug_number = db.Column(db.Integer, unique=True, nullable=False)
    task_id = db.Column(db.Integer, db.ForeignKey('task.id'), nullable=True, ondelete='SET NULL', onupdate='CASCADE')
    description = db.Column(db.Text, nullable=False)
    steps_to_reproduce = db.Column(db.Text)
    severity = db.Column(db.String(50), default='Medium', nullable=False)
    status = db.Column(db.String(50), default='Open', nullable=False)
    created_at = db.Column(db.DateTime, default=datetime.utcnow)
    updated_at = db.Column(db.DateTime, default=datetime.utcnow, onupdate=datetime.utcnow)
    task = db.relationship("Task", backref="bugs", passive_updates=True)

    def __repr__(self):
        return f'<Bug {self.description}>'

class Idea(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    idea_number = db.Column(db.Integer, unique=True, nullable=False)
    task_id = db.Column(db.Integer, db.ForeignKey('task.id'), nullable=True, ondelete='SET NULL', onupdate='CASCADE')
    description = db.Column(db.Text, nullable=False)
    priority = db.Column(db.String(50), default='Medium', nullable=False)
    status = db.Column(db.String(50), default='Open', nullable=False)
    created_at = db.Column(db.DateTime, default=datetime.utcnow)
    updated_at = db.Column(db.DateTime, default=datetime.utcnow, onupdate=datetime.utcnow)
    task = db.relationship("Task", backref="ideas", passive_updates=True)

    def __repr__(self):
        return f'<Idea {self.description}>'

