    status = SelectField('Status', choices=[('Open', 'Open'), ('In Progress', 'In Progress'), ('Resolved', 'Resolved'), ('Closed', 'Closed')])
    submit = SubmitField('Save Task')

class BugForm(FlaskForm):
    bug_number = IntegerField('Bug Number', validators=[InputRequired(), NumberRange(min=1)])
    task_id = IntegerField('Task ID', validators=[NumberRange(min=0)])  # Allow unassigned bugs
    description = TextAreaField('Description', validators=[InputRequired()])
    steps_to_reproduce = TextAreaField('Steps to Reproduce')
    severity = SelectField('Severity', choices=[('Low', 'Low'), ('Medium', 'Medium'), ('High', 'High'), ('Critical', 'Critical')])
    status = SelectField('Status', choices=[('Open', 'Open'), ('In Progress', 'In Progress'), ('Resolved', 'Resolved'), ('Closed', 'Closed')])
