```text
### FILENAME: server.js
const express = require('express');
const sqlite3 = require('sqlite3').verbose();
const cors = require('cors');

const app = express();
const port = 3001;

app.use(cors());
app.use(express.json());

const db = new sqlite3.Database('./database.db', (err) => {
  if (err) {
    console.error(err.message);
  }
  console.log('Connected to the SQLite database.');
});

db.serialize(() => {
  db.run(`
    CREATE TABLE IF NOT EXISTS items (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        item_type TEXT NOT NULL, -- 'bug', 'idea', 'task'
        name TEXT NOT NULL,
        description TEXT,
        status TEXT NOT NULL, -- 'open', 'in progress', 'resolved', 'closed'
        priority TEXT DEFAULT 'medium', -- 'high', 'medium', 'low'
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
    )
  `);
});

// API Endpoints
app.get('/items', (req, res) => {
  db.all('SELECT * FROM items', (err, rows) => {
    if (err) {
      console.error(err.message);
      res.status(500).json({ error: err.message });
      return;
    }
    res.json(rows);
  });
});

app.post('/items', (req, res) => {
  const { item_type, name, description, status } = req.body;
  const sql = `INSERT INTO items (item_type, name, description, status) VALUES (?, ?, ?, ?)`;
  db.run(sql, [item_type, name, description, status], function(err) {
    if (err) {
      console.error(err.message);
      res.status(500).json({ error: err.message });
      return;
    }
    res.json({ id: this.lastID });
  });
});

app.put('/items/:id', (req, res) => {
  const { id } = req.params;
  const { name, description, status } = req.body;
  const sql = `UPDATE items SET name = ?, description = ?, status = ? WHERE id = ?`;
  db.run(sql, [name, description, status, id], function(err) {
    if (err) {
      console.error(err.message);
      res.status(500).json({ error: err.message });
      return;
    }
    res.json({ message: 'Item updated successfully' });
  });
});

app.delete('/items/:id', (req, res) => {
  const { id } = req.params;
  const sql = `DELETE FROM items WHERE id = ?`;
  db.run(sql, [id], function(err) {
    if (err) {
      console.error(err.message);
      res.status(500).json({ error: err.message });
      return;
    }
    res.json({ message: 'Item deleted successfully' });
  });
});

app.listen(port, () => {
  console.log(`Server listening on port ${port}`);
});
```

