import React, { useState, useEffect } from 'react';
import './App.css';
import { FiPlus, FiEdit, FiDelete } from 'react-icons/fi';

function App() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({
    item_type: 'bug',
    name: '',
    description: '',
    status: 'open',
  });

  useEffect(() => {
    fetch('http://localhost:3001/items')
      .then(response => response.json())
      .then(data => setItems(data));
  }, []);

  const addItem = () => {
    fetch('http://localhost:3001/items', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newItem),
    })
      .then(response => response.json())
      .then(data => {
        setItems([...items, data]);
        setNewItem({ item_type: 'bug', name: '', description: '', status: 'open' });
      });
  };

  const updateItem = (id, updatedItem) => {
    fetch(`http://localhost:3001/items/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedItem),
    })
      .then(response => response.json())
      .then(data => {
        setItems(items.map(item =>
          item.id === id ? { ...item, ...updatedItem } : item
        ));
      });
  };

  const deleteItem = (id) => {
    fetch(`http://localhost:3001/items/${id}`, {
      method: 'DELETE',
    })
      .then(response => response.json())
      .then(data => {
        setItems(items.filter(item => item.id !== id));
      });
  };

  return (
    <div className="bg-gray-900 min-h-screen py-10 px-4">
      <div className="container mx-auto">
        <h1 className="text-3xl font-bold text-white mb-6">Todo List</h1>

        <div className="bg-glass rounded-lg shadow-xl p-6 mb-8">
          <h2 className="text-xl font-semibold text-white mb-4">Add New Item</h2>
          <div className="mb-4">
            <label htmlFor="item_type" className="text-white">Item Type:</label>
            <select
              id="item_type"
              className="block w-full mt-1 bg-transparent border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              value={newItem.item_type}
              onChange={(e) => setNewItem({...newItem, item_type: e.target.value})}
            >
              <option value="bug">Bug</option>
              <option value="idea">Idea</option>
              <option value="task">Task</option>
            </select>
          </div>
          <div className="mb-4">
            <label htmlFor="name" className="text-white">Name:</label>
            <input
              type="text"
              id="name"
              className="block w-full mt-1 bg-transparent border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              value={newItem.name}
              onChange={(e) => setNewItem({...newItem, name: e.target.value})}
            />
          </div>
          <div className="mb-4">
            <label htmlFor="description" className="text-white">Description:</label>
            <textarea
              id="description"
              className="block w-full mt-1 bg-transparent border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              value={newItem.description}
              onChange={(e) => setNewItem({...newItem, description: e.target.value})}
            />
          </div>
          <div className="mb-4">
            <label htmlFor="status" className="text-white">Status:</label>
            <select
              id="status"
              className="block w-full mt-1 bg-transparent border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              value={newItem.status}
              onChange={(e) => setNewItem({...newItem, status: e.target.value})}
            >
              <option value="open">Open</option>
              <option value="in progress">In Progress</option>
              <option value="resolved">Resolved</option>
              <option value="closed">Closed</option>
            </select>
          </div>
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            onClick={addItem}
          >
            <FiPlus className="mr-2"/>
            Add Item
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {items.map(item => (
            <div key={item.id} className="bg-glass rounded-lg shadow-xl p-6">
              <h2 className="text-xl font-semibold text-white mb-2">{item.name}</h2>
              <p className="text-gray-300 mb-2">{item.description}</p>
              <p className="text-gray-300 mb-2">Status: {item.status}</p>
              <div className="flex space-x-2">
                <button onClick={() => updateItem(item.id, { status: 'in progress' })} className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-2 rounded">
                  In Progress
                </button>
                <button onClick={() => updateItem(item.id, { status: 'resolved' })} className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-1 px-2 rounded">
                  Resolve
                </button>
                <button onClick={() => deleteItem(item.id)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded">
                  <FiDelete className="mr-1"/>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
