const express = require('express');
const sqlite3 = require('sqlite3').verbose();
const cors = require('cors');

const app = express();
const port = 3001;

app.use(cors());
app.use(express.json());

const db = new sqlite3.Database('./database.db', (err) => {
    if (err) {
        console.error(err.message);
    }
    console.log('Connected to the database.');
});

// Create table if it doesn't exist
db.serialize(() => {
    db.run(`
        CREATE TABLE IF NOT EXISTS items (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            item_type TEXT NOT NULL,
            name TEXT NOT NULL,
            description TEXT,
            status TEXT NOT NULL,
            priority TEXT DEFAULT 'medium',
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
        )
    `);
});

// Get all items
app.get('/items', (req, res) => {
    db.all('SELECT * FROM items', (err, rows) => {
        if (err) {
            console.error(err.message);
            res.status(500).json({ error: 'Failed to fetch items' });
            return;
        }
        res.json(rows);
    });
});

// Get item by id
app.get('/items/:id', (req, res) => {
    const id = req.params.id;
    db.get('SELECT * FROM items WHERE id = ?', [id], (err, row) => {
        if (err) {
            console.error(err.message);
            res.status(500).json({ error: 'Failed to fetch item' });
            return;
        }
        if (!row) {
            res.status(404).json({ error: 'Item not found' });
            return;
        }
        res.json(row);
    });
});


// Create a new item
app.post('/items', (req, res) => {
    const { item_type, name, description, status } = req.body;
    const sql = `INSERT INTO items (item_type, name, description, status) VALUES (?, ?, ?, ?)`;
    db.run(sql, [item_type, name, description, status], function(err) {
        if (err) {
            console.error(err.message);
            res.status(500).json({ error: 'Failed to create item' });
            return;
        }
        res.json({ id: this.lastID, item_type, name, description, status });
    });
});

// Update an item
app.put('/items/:id', (req, res) => {
    const id = req.params.id;
    const { name, description, status } = req.body;
    const sql = `UPDATE items SET name = ?, description = ?, status = ? WHERE id = ?`;
    db.run(sql, [name, description, status, id], function(err) {
        if (err) {
            console.error(err.message);
            res.status(500).json({ error: 'Failed to update item' });
            return;
        }
        res.json({ id: id, name, description, status });
    });
});

// Delete an item
