### FILENAME: client/src/components/TodoList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TodoList = () => {
    const [items, setItems] = useState([]);
    const [newItem, setNewItem] = useState({
        item_type: 'bug',
        name: '',
        description: '',
        status: 'open',
    });

    useEffect(() => {
        axios.get('http://localhost:3001/items')
            .then(response => {
                setItems(response.data);
            })
            .catch(error => {
                console.error('Error fetching items:', error);
            });
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewItem({ ...newItem, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/items', newItem);
            setItems([...items, response.data]);
            setNewItem({ item_type: 'bug', name: '', description: '', status: 'open' });
        } catch (error) {
            console.error('Error creating item:', error);
        }
    };

    const handleUpdateStatus = async (id, newStatus) => {
        try {
            await axios.put(`http://localhost:3001/items/${id}`, { status: newStatus });
            setItems(items.map(item =>
                item.id === id ? { ...item, status: newStatus } : item
            ));
        } catch (error) {
            console.error('Error updating status:', error);
        }
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Todo List</h1>
            <form onSubmit={handleSubmit} className="mb-4">
                <input type="text" name="name" placeholder="Name" value={newItem.name} onChange={handleInputChange} className="border rounded p-2 mr-2" />
                <input type="text" name="description" placeholder="Description" value={newItem.description} onChange={handleInputChange} className="border rounded p-2 mr-2" />
                <select name="item_type" value={newItem.item_type} onChange={handleInputChange} className="border rounded p-2 mr-2">
                    <option value="bug">Bug</option>
                    <option value="idea">Idea</option>
                    <option value="task">Task</option>
                </select>
                <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                    Add Item
                </button>
            </form>
            <table className="w-full border">
                <thead>
                    <tr>
                        <th className="border p-2">ID</th>
                        <th className="border p-2">Name</th>
                        <th className="border p-2">Description</th>
                        <th className="border p-2">Type</th>
                        <th className="border p-2">Status</th>
                        <th className="border p-2">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {items.map(item => (
                        <tr key={item.id} className="border">
                            <td className="border p-2">{item.id}</td>
                            <td className="border p-2">{item.name}</td>
                            <td className="border p-2">{item.description}</td>
                            <td className="border p-2">{item.item_type}</td>
                            <td className="border p-2">{item.status}</td>
                            <td className="border p-2">
                                <button onClick={() => handleUpdateStatus(item.id, item.status === 'open' ? 'in progress' : 'open')} className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-2 rounded">
                                    Toggle Status
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TodoList;
```

