import os
import sqlite3
from pathlib import Path

DB_SCHEMA = """
PRAGMA foreign_keys = ON;

CREATE TABLE IF NOT EXISTS status (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL UNIQUE,
    description TEXT
);

CREATE TABLE IF NOT EXISTS todo_item (
    nr INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    description TEXT,
    type TEXT NOT NULL CHECK (type IN ('bug','idee')),
    status_id INTEGER NOT NULL,
    created_at DATETIME NOT NULL DEFAULT (datetime('now')),
    updated_at DATETIME NOT NULL DEFAULT (datetime('now')),
    CONSTRAINT fk_status FOREIGN KEY (status_id) REFERENCES status(id)
        ON UPDATE CASCADE ON DELETE RESTRICT
);

CREATE TRIGGER IF NOT EXISTS trg_todo_item_updated
AFTER UPDATE ON todo_item
FOR EACH ROW
BEGIN
    UPDATE todo_item SET updated_at = datetime('now') WHERE nr = NEW.nr;
END;
"""

DEFAULT_STATUSES = [
    ("Offen", "Aufgabe wurde noch nicht bearbeitet"),
    ("In Bearbeitung", "Arbeit lÃ¤uft gerade"),
    ("Erledigt", "Aufgabe ist abgeschlossen")
]

class Database:
    """Simple wrapper around sqlite3 providing the required schema and CRUD helpers."""
    def __init__(self, db_path: str = "todo.db"):
        self.db_path = Path(db_path)
        self.conn = sqlite3.connect(self.db_path)
        self.conn.row_factory = sqlite3.Row
        self._initialize()

    def _initialize(self):
        """Create tables, triggers and default status entries."""
        cur = self.conn.cursor()
        cur.executescript(DB_SCHEMA)
        # Insert default statuses if they do not exist
        cur.executemany(
            "INSERT OR IGNORE INTO status (name, description) VALUES (?, ?)",
            DEFAULT_STATUSES
        )
        self.conn.commit()

    def get_connection(self):
        return self.conn

    def close(self):
        self.conn.close()

# Helper to obtain a singleton DB instance used by the app
def get_db(db_path: str = "todo.db") -> Database:
    """Return a Database instance. In production a single file is used,
    while tests can pass ':memory:' or a temporary path."""
    if not hasattr(get_db, "_instance"):
        get_db._instance = Database(db_path)
    return get_db._instance

