class TodoModel:
    """Highâ€‘level CRUD operations for todo items."""

    @staticmethod
    def _dict_from_row(row: sqlite3.Row) -> dict:
        return dict(row)

    @classmethod
    def get_statuses(cls) -> List[Tuple[int, str]]:
        """Return list of (id, name) for all statuses."""
        db = get_db()
        cur = db.get_connection().cursor()
        cur.execute("SELECT id, name FROM status ORDER BY id")
        return [(row["id"], row["name"]) for row in cur.fetchall()]

    @classmethod
    def get_status_id_by_name(cls, name: str) -> Optional[int]:
        db = get_db()
        cur = db.get_connection().cursor()
        cur.execute("SELECT id FROM status WHERE name = ?", (name,))
        row = cur.fetchone()
        return row["id"] if row else None

    @classmethod
    def get_items(cls) -> List[dict]:
        """Return all todo items with joined status name."""
        db = get_db()
        cur = db.get_connection().cursor()
        cur.execute("""
            SELECT ti.nr, ti.name, ti.description, ti.type, s.name AS status,
                   ti.created_at, ti.updated_at
            FROM todo_item ti
            JOIN status s ON ti.status_id = s.id
            ORDER BY ti.nr ASC
        """)
        return [cls._dict_from_row(row) for row in cur.fetchall()]

    @classmethod
    def add_item(cls, name: str, description: str, item_type: str, status_name: str = "Offen") -> int:
