class TodoDialog(QDialog):
    """Dialog for creating or editing a Todo (Bug / Idee)."""

    def __init__(self, parent=None, mode="add", item=None):
        """
        mode: 'add' or 'edit'
        item: dict with keys matching DB columns (required for edit)
        """
        super().__init__(parent)
        self.setWindowTitle("Eintrag hinzufÃ¼gen" if mode == "add" else "Eintrag bearbeiten")
        self.mode = mode
        self.item = item

        self._build_ui()
        if mode == "edit" and item:
            self._populate_fields(item)

    def _build_ui(self):
        layout = QVBoxLayout(self)

        form = QFormLayout()
        self.name_edit = QLineEdit()
        self.desc_edit = QTextEdit()
        self.type_combo = QComboBox()
        self.type_combo.addItems(["bug", "idee"])
        self.status_combo = QComboBox()
        self.status_combo.addItems([name for _, name in TodoModel.get_statuses()])

        form.addRow("Name:", self.name_edit)
        form.addRow("Beschreibung:", self.desc_edit)
        form.addRow("Typ:", self.type_combo)
        form.addRow("Status:", self.status_combo)

        layout.addLayout(form)

        btn_layout = QHBoxLayout()
        self.save_btn = QPushButton("Speichern")
        self.cancel_btn = QPushButton("Abbrechen")
        btn_layout.addStretch()
        btn_layout.addWidget(self.save_btn)
        btn_layout.addWidget(self.cancel_btn)

        layout.addLayout(btn_layout)

        self.save_btn.clicked.connect(self.accept)
        self.cancel_btn.clicked.connect(self.reject)

        # Apply a simple glassâ€‘morphism style
        self.setStyleSheet("""
            QDialog {
                background: rgba(255, 255, 255, 0.25);
                border-radius: 12px;
                backdrop-filter: blur(10px);
                padding: 15px;
            }
            QLabel { color: #222; }
            QLineEdit, QTextEdit, QComboBox {
                background: rgba(255,255,255,0.6);
                border: 1px solid rgba(0,0,0,0.2);
                border-radius: 6px;
                padding: 4px;
            }
            QPushButton {
                background: rgba(0,120,215,0.7);
                color: white;
                border: none;
                border-radius: 6px;
                padding: 6px 12px;
            }
            QPushButton:hover {
                background: rgba(0,120,215,0.9);
            }
        """)

    def _populate_fields(self, item: dict):
        self.name_edit.setText(item["name"])
        self.desc_edit.setPlainText(item["description"] or "")
        self.type_combo.setCurrentText(item["type"])
        self.status_combo.setCurrentText(item["status"])

    def get_data(self) -> dict:
        return {
            "name": self.name_edit.text().strip(),
            "description": self.desc_edit.toPlainText().strip(),
            "type": self.type_combo.currentText(),
            "status": self.status_combo.currentText()
        }

    def accept(self):
        data = self.get_data()
        if not data["name"]:
            QMessageBox.warning(self, "Eingabefehler", "Der Name darf nicht leer sein.")
            return
        super().accept()

