"""
Database module for Todo List App
Handles SQLite operations with proper parameterized queries (SQL Injection safe)
"""
import sqlite3
from datetime import datetime
from typing import List, Optional, Dict, Any
from dataclasses import dataclass
from contextlib import contextmanager
import os


@dataclass
class Status:
    status_id: int
    status_name: str


@dataclass
class Task:
    task_id: int
    title: str
    type: str  # 'bug' or 'idea'
    description: Optional[str]
    status_id: int
    status_name: str
    created_at: str


class DatabaseManager:
    def __init__(self, db_path: str = "todo.db"):
        self.db_path = db_path
        self.init_database()

    @contextmanager
    def _get_connection(self):
        """Context manager for database connections"""
        conn = sqlite3.connect(self.db_path)
        conn.row_factory = sqlite3.Row
        try:
            yield conn
            conn.commit()
        except Exception as e:
            conn.rollback()
            raise e
        finally:
            conn.close()

    def init_database(self):
        """Initialize database with schema and seed data"""
        with self._get_connection() as conn:
            cursor = conn.cursor()

            # Status table
            cursor.execute("""
                CREATE TABLE IF NOT EXISTS status (
                    status_id INTEGER PRIMARY KEY AUTOINCREMENT,
                    status_name TEXT NOT NULL UNIQUE CHECK(LENGTH(status_name) <= 20)
                )
            """)

            # Tasks table
            cursor.execute("""
                CREATE TABLE IF NOT EXISTS tasks (
                    task_id INTEGER PRIMARY KEY AUTOINCREMENT,
                    title TEXT NOT NULL CHECK(LENGTH(title) BETWEEN 3 AND 100),
                    type TEXT NOT NULL CHECK(type IN ('bug', 'idea')),
                    description TEXT CHECK(LENGTH(description) <= 1000),
                    status_id INTEGER NOT NULL,
                    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                    FOREIGN KEY (status_id) 
                        REFERENCES status(status_id)
                        ON UPDATE CASCADE 
                        ON DELETE RESTRICT
                )
            """)

            # Indexes for performance
            cursor.execute("CREATE INDEX IF NOT EXISTS idx_status ON tasks(status_id)")
            cursor.execute("CREATE INDEX IF NOT EXISTS idx_task_type ON tasks(type)")
            cursor.execute("CREATE INDEX IF NOT EXISTS idx_created ON tasks(created_at)")

            # Seed initial status values
            cursor.execute("SELECT COUNT(*) FROM status")
            if cursor.fetchone()[0] == 0:
                initial_status = ['Offen', 'In Bearbeitung', 'Abgeschlossen', 'Zur√ºckgestellt']
                for status in initial_status:
                    cursor.execute("INSERT INTO status (status_name) VALUES (?)", (status,))

    def create_task(self, title: str, task_type: str, description: Optional[str], 
                   status_id: int = 1) -> int:
        """Create a new task and return its ID"""
        if task_type not in ('bug', 'idea'):
            raise ValueError("Type must be 'bug' or 'idea'")

        if not (3 <= len(title) <= 100):
            raise ValueError("Title must be between 3 and 100 characters")

        with self._get_connection() as conn:
            cursor = conn.cursor()
            cursor.execute("""
                INSERT INTO tasks (title, type, description, status_id)
                VALUES (?, ?, ?, ?)
            """, (title, task_type, description, status_id))
            return cursor.lastrowid

    def get_all_tasks(self, task_type: Optional[str] = None, 
                     status_id: Optional[int] = None) -> List[Task]:
        """Retrieve all tasks with optional filtering"""
        query = """
            SELECT t.task_id, t.title, t.type, t.description, 
                   t.status_id, s.status_name, t.created_at
            FROM tasks t
            JOIN status s ON t.status_id = s.status_id
            WHERE 1=1
        """
        params = []

        if task_type:
            query += " AND t.type = ?"
            params.append(task_type)

        if status_id:
            query += " AND t.status_id = ?"
            params.append(status_id)

        query += " ORDER BY t.created_at DESC"

        with self._get_connection() as conn:
            cursor = conn.cursor()
            cursor.execute(query, params)
            rows = cursor.fetchall()
            return [Task(
                task_id=row['task_id'],
                title=row['title'],
                type=row['type'],
                description=row['description'],
                status_id=row['status_id'],
                status_name=row['status_name'],
                created_at=row['created_at']
            ) for row in rows]

    def update_task_status(self, task_id: int, new_status_id: int) -> bool:
        """Update the status of a task"""
        with self._get_connection() as conn:
            cursor = conn.cursor()
            cursor.execute("""
                UPDATE tasks SET status_id = ? WHERE task_id = ?
            """, (new_status_id, task_id))
            return cursor.rowcount > 0

    def delete_task(self, task_id: int) -> bool:
        """Delete a task by ID"""
        with self._get_connection() as conn:
            cursor = conn.cursor()
            cursor.execute("DELETE FROM tasks WHERE task_id = ?", (task_id,))
            return cursor.rowcount > 0

    def get_all_status(self) -> List[Status]:
        """Get all available status values"""
        with self._get_connection() as conn:
            cursor = conn.cursor()
            cursor.execute("SELECT status_id, status_name FROM status ORDER BY status_id")
            rows = cursor.fetchall()
            return [Status(status_id=row['status_id'], status_name=row['status_name']) 
                    for row in rows]

    def search_tasks(self, search_term: str) -> List[Task]:
        """Search tasks by title or description"""
        search_pattern = f"%{search_term}%"
        with self._get_connection() as conn:
            cursor = conn.cursor()
            cursor.execute("""
                SELECT t.task_id, t.title, t.type, t.description, 
                       t.status_id, s.status_name, t.created_at
                FROM tasks t
                JOIN status s ON t.status_id = s.status_id
                WHERE t.title LIKE ? OR t.description LIKE ?
                ORDER BY t.created_at DESC
            """, (search_pattern, search_pattern))
            rows = cursor.fetchall()
            return [Task(
                task_id=row['task_id'],
                title=row['title'],
                type=row['type'],
                description=row['description'],
                status_id=row['status_id'],
                status_name=row['status_name'],
                created_at=row['created_at']
            ) for row in rows]
