"""
Custom UI Components for Glassmorphism Design
"""
from PyQt5.QtWidgets import (QFrame, QPushButton, QGraphicsDropShadowEffect, 
                             QHBoxLayout, QLabel, QWidget, QComboBox)
from PyQt5.QtCore import Qt, QPropertyAnimation, QPoint, QEasingCurve, QSize
from PyQt5.QtGui import QColor, QIcon, QFont


class GlassCard(QFrame):
    """A card with glassmorphism effect"""
    def __init__(self, parent=None):
        super().__init__(parent)
        self.setObjectName("glassCard")
        self.setFrameShape(QFrame.StyledPanel)

        # Add shadow effect
        shadow = QGraphicsDropShadowEffect(self)
        shadow.setBlurRadius(20)
        shadow.setColor(QColor(0, 0, 0, 80))
        shadow.setOffset(0, 4)
        self.setGraphicsEffect(shadow)

        # Enable hover events
        self.setAttribute(Qt.WA_Hover, True)


class AnimatedButton(QPushButton):
    """Button with hover animation"""
    def __init__(self, text, parent=None, button_type="default"):
        super().__init__(text, parent)
        self.button_type = button_type
        self.setObjectName(f"{button_type}Button" if button_type != "default" else "")

        # Animation for size
        self.animation = QPropertyAnimation(self, b"size")
        self.animation.setDuration(150)
        self.animation.setEasingCurve(QEasingCurve.OutCubic)

        self.base_size = QSize(100, 35)
        self.hover_size = QSize(105, 38)

    def enterEvent(self, event):
        self.animation.setStartValue(self.size())
        self.animation.setEndValue(self.hover_size)
        self.animation.start()
        super().enterEvent(event)

    def leaveEvent(self, event):
        self.animation.setStartValue(self.size())
        self.animation.setEndValue(self.base_size)
        self.animation.start()
        super().leaveEvent(event)


class StatusBadge(QLabel):
    """Colored badge for status display"""
    COLORS = {
        'Offen': ('#F59E0B', 'white'),
        'In Bearbeitung': ('#3B82F6', 'white'),
        'Abgeschlossen': ('#10B981', 'white'),
        'Zur√ºckgestellt': ('#6B7280', 'white')
    }

    def __init__(self, status_text, parent=None):
        super().__init__(status_text, parent)
        self.setAlignment(Qt.AlignCenter)
        self.setFixedHeight(28)
        self.setStyleSheet(self._get_style(status_text))

    def _get_style(self, status):
        bg_color, text_color = self.COLORS.get(status, ('#6B7280', 'white'))
        return f"""
            QLabel {{
                background-color: {bg_color};
                color: {text_color};
                border-radius: 14px;
                padding: 4px 12px;
                font-size: 11px;
                font-weight: 600;
                min-width: 100px;
            }}
        """


class TypeBadge(QLabel):
    """Badge for Bug/Idea type"""
    COLORS = {
        'bug': ('#EF4444', 'white'),
        'idea': ('#8B5CF6', 'white')
    }

    def __init__(self, type_text, parent=None):
        super().__init__(type_text.upper(), parent)
        self.setAlignment(Qt.AlignCenter)
        self.setFixedHeight(24)
        self.setStyleSheet(self._get_style(type_text))

    def _get_style(self, task_type):
        bg_color, text_color = self.COLORS.get(task_type, ('#6B7280', 'white'))
        return f"""
            QLabel {{
                background-color: {bg_color};
                color: {text_color};
                border-radius: 12px;
                padding: 2px 10px;
                font-size: 10px;
                font-weight: 700;
                letter-spacing: 0.5px;
                min-width: 50px;
            }}
        """


class TaskRow(QWidget):
    """Custom widget for task display in list"""
    def __init__(self, task, status_list, parent=None):
        super().__init__(parent)
        self.task = task
        self.status_list = status_list
        self.setup_ui()

    def setup_ui(self):
        layout = QHBoxLayout(self)
        layout.setContentsMargins(15, 10, 15, 10)
        layout.setSpacing(20)

        # ID
        id_label = QLabel(f"#{self.task.task_id:03d}")
        id_label.setStyleSheet("color: rgba(255,255,255,0.5); font-weight: 600; font-size: 12px;")
        id_label.setFixedWidth(50)
        layout.addWidget(id_label)

        # Type Badge
        type_badge = TypeBadge(self.task.type)
        layout.addWidget(type_badge)

        # Title
        title_label = QLabel(self.task.title)
        title_label.setStyleSheet("color: white; font-weight: 500; font-size: 13px;")
        title_label.setMinimumWidth(200)
        layout.addWidget(title_label, 1)

        # Description (truncated)
        if self.task.description:
            desc = self.task.description[:60] + "..." if len(self.task.description) > 60 else self.task.description
        else:
            desc = "-"
        desc_label = QLabel(desc)
        desc_label.setStyleSheet("color: rgba(255,255,255,0.6); font-size: 12px;")
        desc_label.setMinimumWidth(200)
        layout.addWidget(desc_label, 2)

        # Status Combo
        self.status_combo = QComboBox()
        self.status_combo.setFixedWidth(150)
        for status in self.status_list:
            self.status_combo.addItem(status.status_name, status.status_id)

        # Set current status
        index = self.status_combo.findData(self.task.status_id)
        if index >= 0:
            self.status_combo.setCurrentIndex(index)

        self.status_combo.currentIndexChanged.connect(self.on_status_changed)
        layout.addWidget(self.status_combo)

        # Delete Button
        self.delete_btn = AnimatedButton("üóë", button_type="danger")
        self.delete_btn.setFixedSize(35, 35)
        self.delete_btn.setToolTip("Delete")
        layout.addWidget(self.delete_btn)

        self.setStyleSheet("""
            QWidget {
                background-color: rgba(255, 255, 255, 0.05);
                border-radius: 10px;
            }
            QWidget:hover {
                background-color: rgba(255, 255, 255, 0.1);
            }
        """)

    def on_status_changed(self):
        new_status_id = self.status_combo.currentData()
        if new_status_id != self.task.status_id:
            # Emit signal or callback to parent
            if hasattr(self.parent(), 'status_changed'):
                self.parent().status_changed(self.task.task_id, new_status_id)
