        assert len(status_2) == 1

    def test_search_tasks(self, temp_db):
        """Test search functionality"""
        temp_db.create_task("Alpha Bug", "bug", "Description with gamma", 1)
        temp_db.create_task("Beta Feature", "idea", "Another description", 1)
        temp_db.create_task("Gamma Issue", "bug", "No match here", 1)

        results = temp_db.search_tasks("alpha")
        assert len(results) == 1
        assert results[0].title == "Alpha Bug"

        results = temp_db.search_tasks("gamma")
        assert len(results) == 2  # In title and description

    def test_sql_injection_protection(self, temp_db):
        """Test that parameterized queries prevent SQL injection"""
        malicious_title = "'; DROP TABLE tasks; --"
        task_id = temp_db.create_task(malicious_title, "bug", "Desc", 1)

        # If injection worked, this would fail
        tasks = temp_db.get_all_tasks()
        assert len(tasks) == 1
        assert tasks[0].title == malicious_title
