"""
Custom UI Components for Glassmorphism Design
"""
from PyQt5.QtWidgets import (QFrame, QPushButton, QGraphicsDropShadowEffect, 
                             QHBoxLayout, QLabel, QWidget, QComboBox)
from PyQt5.QtCore import Qt, QPropertyAnimation, QPoint, QEasingCurve, QSize
from PyQt5.QtGui import QColor, QIcon, QFont


class GlassCard(QFrame):
    """A card with glassmorphism effect"""
    def __init__(self, parent=None):
        super().__init__(parent)
        self.setObjectName("glassCard")
        self.setFrameShape(QFrame.StyledPanel)

        # Add shadow effect
        shadow = QGraphicsDropShadowEffect(self)
        shadow.setBlurRadius(20)
        shadow.setColor(QColor(0, 0, 0, 80))
        shadow.setOffset(0, 4)
        self.setGraphicsEffect(shadow)

        # Enable hover events
        self.setAttribute(Qt.WA_Hover, True)


class AnimatedButton(QPushButton):
    """Button with hover animation"""
    def __init__(self, text, parent=None, button_type="default"):
        super().__init__(text, parent)
        self.button_type = button_type
        self.setObjectName(f"{button_type}Button" if button_type != "default" else "")

        # Animation for size
        self.animation = QPropertyAnimation(self, b"size")
        self.animation.setDuration(150)
        self.animation.setEasingCurve(QEasingCurve.OutCubic)

        self.base_size = QSize(100, 35)
        self.hover_size = QSize(105, 38)

    def enterEvent(self, event):
        self.animation.setStartValue(self.size())
        self.animation.setEndValue(self.hover_size)
        self.animation.start()
        super().enterEvent(event)

    def leaveEvent(self, event):
        self.animation.setStartValue(self.size())
        self.animation.setEndValue(self.base_size)
        self.animation.start()
        super().leaveEvent(event)


class StatusBadge(QLabel):
    """Colored badge for status display"""
    COLORS = {
        'Offen': ('#F59E0B', 'white'),
