    def populate_table(self, tasks):
        self.table.setRowCount(len(tasks))
        statuses = self.db.get_all_status()

        for row, task in enumerate(tasks):
            # NR
            nr_item = QTableWidgetItem(f"#{task.task_id:03d}")
            nr_item.setTextAlignment(Qt.AlignCenter)
            nr_item.setFlags(nr_item.flags() & ~Qt.ItemIsEditable)
            self.table.setItem(row, 0, nr_item)

            # Type Badge (as widget)
            type_widget = TypeBadge(task.type)
            self.table.setCellWidget(row, 1, type_widget)

            # Title - SEC-002 FIX: Escape HTML to prevent XSS
            safe_title = html.escape(str(task.title))
            title_item = QTableWidgetItem(safe_title)
            title_item.setFlags(title_item.flags() & ~Qt.ItemIsEditable)
            # Explicitly set text format to plain text to prevent HTML interpretation
            title_item.setData(Qt.DisplayRole, safe_title)
            self.table.setItem(row, 2, title_item)

            # Description - SEC-002 FIX: Escape HTML to prevent XSS
            desc = task.description or "-"
            if len(desc) > 100:
                desc = desc[:97] + "..."
            safe_desc = html.escape(str(desc))
            desc_item = QTableWidgetItem(safe_desc)
            desc_item.setFlags(desc_item.flags() & ~Qt.ItemIsEditable)
            desc_item.setData(Qt.DisplayRole, safe_desc)
            self.table.setItem(row, 3, desc_item)

            # Status Combo
            status_combo = QComboBox()
            for status in statuses:
                status_combo.addItem(status.status_name, status.status_id)

            index = status_combo.findData(task.status_id)
            if index >= 0:
                status_combo.setCurrentIndex(index)

            # Connect with lambda to capture task_id
            status_combo.currentIndexChanged.connect(
                lambda idx, combo=status_combo, tid=task.task_id: 
                self.on_status_change(tid, combo.currentData())
            )
            self.table.setCellWidget(row, 4, status_combo)

            # Delete Button
            delete_btn = AnimatedButton("üóë", button_type="danger")
            delete_btn.setFixedSize(35, 35)
            delete_btn.clicked.connect(lambda checked, tid=task.task_id: self.delete_task(tid))
            self.table.setCellWidget(row, 5, delete_btn)

            # Row height
            self.table.setRowHeight(row, 50)

    def on_status_change(self, task_id, new_status_id):
        """Handle status change in table"""
        if self.db.update_task_status(task_id, new_status_id):
            self.status_bar.setText(f"Status von Task #{task_id:03d} aktualisiert")

    def delete_task(self, task_id):
