import pytest
import os
import tempfile
from database import DatabaseManager, Task, Status


@pytest.fixture
def temp_db():
    """Create a temporary database for testing"""
    fd, path = tempfile.mkstemp(suffix='.db')
    os.close(fd)
    db = DatabaseManager(path)
    yield db
    os.unlink(path)


class TestDatabaseManager:
    def test_initialization_creates_tables(self, temp_db):
        """Test that initialization creates all necessary tables"""
        # If init runs without error, tables exist
        tasks = temp_db.get_all_tasks()
        assert isinstance(tasks, list)

    def test_initial_status_seeded(self, temp_db):
        """Test that initial status values are present"""
        statuses = temp_db.get_all_status()
        status_names = [s.status_name for s in statuses]

        assert len(statuses) == 4
        assert 'Offen' in status_names
        assert 'In Bearbeitung' in status_names
        assert 'Abgeschlossen' in status_names
        assert 'Zur√ºckgestellt' in status_names

    def test_create_task_success(self, temp_db):
        """Test successful task creation"""
        task_id = temp_db.create_task(
            title="Test Bug",
            task_type="bug",
            description="Test Description",
            status_id=1
        )

        assert task_id > 0
        tasks = temp_db.get_all_tasks()
        assert len(tasks) == 1
        assert tasks[0].title == "Test Bug"
        assert tasks[0].type == "bug"

    def test_create_task_invalid_type(self, temp_db):
        """Test that invalid task type raises error"""
        with pytest.raises(ValueError, match="Type must be"):
            temp_db.create_task(
                title="Test",
                task_type="invalid",
                description="Test",
                status_id=1
            )

    def test_create_task_title_too_short(self, temp_db):
        """Test that short title raises error"""
        with pytest.raises(ValueError, match="Title must be between"):
            temp_db.create_task(
                title="AB",
                task_type="bug",
                description="Test",
                status_id=1
            )

    def test_create_task_title_too_long(self, temp_db):
        """Test that long title raises error"""
        with pytest.raises(ValueError, match="Title must be between"):
            temp_db.create_task(
                title="A" * 101,
                task_type="bug",
                description="Test",
                status_id=1
            )

    def test_update_task_status(self, temp_db):
        """Test status update"""
        task_id = temp_db.create_task("Test", "idea", "Desc", 1)
        result = temp_db.update_task_status(task_id, 2)

        assert result is True
        tasks = temp_db.get_all_tasks()
        assert tasks[0].status_id == 2

    def test_update_nonexistent_task(self, temp_db):
        """Test updating non-existent task returns False"""
        result = temp_db.update_task_status(9999, 1)
        assert result is False

    def test_delete_task(self, temp_db):
        """Test task deletion"""
        task_id = temp_db.create_task("To Delete", "bug", "Desc", 1)
        result = temp_db.delete_task(task_id)

        assert result is True
        tasks = temp_db.get_all_tasks()
        assert len(tasks) == 0

    def test_delete_nonexistent_task(self, temp_db):
        """Test deleting non-existent task returns False"""
        result = temp_db.delete_task(9999)
        assert result is False

    def test_filter_by_type(self, temp_db):
        """Test filtering tasks by type"""
        temp_db.create_task("Bug 1", "bug", "Desc", 1)
        temp_db.create_task("Idea 1", "idea", "Desc", 1)
        temp_db.create_task("Bug 2", "bug", "Desc", 1)

        bugs = temp_db.get_all_tasks(task_type="bug")
        ideas = temp_db.get_all_tasks(task_type="idea")

        assert len(bugs) == 2
        assert len(ideas) == 1
        assert all(t.type == "bug" for t in bugs)

    def test_filter_by_status(self, temp_db):
        """Test filtering tasks by status"""
        temp_db.create_task("Task 1", "bug", "Desc", 1)
        temp_db.create_task("Task 2", "bug", "Desc", 2)

        status_1 = temp_db.get_all_tasks(status_id=1)
        status_2 = temp_db.get_all_tasks(status_id=2)

        assert len(status_1) == 1
        assert len(status_2) == 1

    def test_search_tasks(self, temp_db):
        """Test search functionality"""
        temp_db.create_task("Alpha Bug", "bug", "Description with gamma", 1)
        temp_db.create_task("Beta Feature", "idea", "Another description", 1)
        temp_db.create_task("Gamma Issue", "bug", "No match here", 1)

        results = temp_db.search_tasks("alpha")
        assert len(results) == 1
        assert results[0].title == "Alpha Bug"

        results = temp_db.search_tasks("gamma")
        assert len(results) == 2  # In title and description

    def test_sql_injection_protection_in_search(self, temp_db):
        """Test that parameterized queries prevent SQL injection in search"""
        malicious_input = "'; DROP TABLE tasks; --"
        # This should not raise an error or drop the table
        results = temp_db.search_tasks(malicious_input)
        assert isinstance(results, list)
        # Verify table still exists by creating a task
        task_id = temp_db.create_task("After Injection Test", "bug", "Desc", 1)
        assert task_id > 0

    def test_sql_injection_protection_in_create(self, temp_db):
        """Test that parameterized queries prevent SQL injection in create"""
        malicious_title = "'; DROP TABLE tasks; --"
        task_id = temp_db.create_task(malicious_title, "bug", "Desc", 1)

        # If injection worked, this would fail
        tasks = temp_db.get_all_tasks()
        assert len(tasks) == 1
        assert tasks[0].title == malicious_title
