"""
Unit Tests for UI Components - XSS and Security Tests included
"""
import pytest
import html
from PyQt5.QtWidgets import QApplication, QTableWidgetItem
from PyQt5.QtCore import Qt
from ui.components import TypeBadge, StatusBadge, GlassCard


# Ensure QApplication exists for widget tests
@pytest.fixture(scope="session")
def qapp():
    """Create QApplication for tests"""
    app = QApplication.instance()
    if app is None:
        app = QApplication([])
    yield app


class TestTypeBadge:
    def test_bug_type_styling(self, qapp):
        """Test bug badge has correct color"""
        badge = TypeBadge("bug")
        assert badge.text() == "BUG"
        # Check that stylesheet contains red color (EF4444)
        assert "EF4444" in badge.styleSheet()

    def test_idea_type_styling(self, qapp):
        """Test idea badge has correct color"""
        badge = TypeBadge("idea")
        assert badge.text() == "IDEA"
        # Check that stylesheet contains purple color (8B5CF6)
        assert "8B5CF6" in badge.styleSheet()


class TestStatusBadge:
    def test_status_colors(self, qapp):
        """Test that different statuses have different colors"""
        badge_open = StatusBadge("Offen")
        badge_done = StatusBadge("Abgeschlossen")

        style_open = badge_open.styleSheet()
        style_done = badge_done.styleSheet()

        # Different colors for different statuses
        assert style_open != style_done
        assert "F59E0B" in style_open  # Orange for Offen
        assert "10B981" in style_done  # Green for Abgeschlossen


class TestGlassCard:
    def test_card_properties(self, qapp):
        """Test glass card has correct initial properties"""
        card = GlassCard()
        assert card.objectName() == "glassCard"
        # Check that graphics effect (shadow) is set
        assert card.graphicsEffect() is not None

    def test_card_hover_enabled(self, qapp):
        """Test that hover is enabled for interactions"""
        card = GlassCard()
        assert card.testAttribute(Qt.WA_Hover)


class TestXSSPrevention:
    """Test XSS prevention in UI components"""

    def test_table_item_escaping(self, qapp):
        """Test that HTML in task titles is escaped and not rendered"""
        malicious_title = "<script>alert('xss')</script>"
        escaped_title = html.escape(malicious_title)

        item = QTableWidgetItem(escaped_title)
        item.setData(Qt.DisplayRole, escaped_title)

        # The text should contain the escaped version, not render as HTML
        assert "<script>" not in item.text()
        assert "&lt;script&gt;" in item.text() or item.text() == escaped_title

    def test_html_injection_prevention(self, qapp):
        """Test that HTML tags are displayed as text, not rendered"""
        html_content = "<b>Bold</b> and <i>Italic</i>"
        escaped = html.escape(html_content)

        item = QTableWidgetItem(escaped)
        item.setData(Qt.DisplayRole, escaped)

        # Should not contain raw HTML tags
        assert item.text() == escaped
        assert "<b>" not in item.text()
        assert "<i>" not in item.text()
