    db.session.commit()

    # Update status
    response = client.post(f'/update_status/{task.nr}', data=dict(
        status_id='2'
    ))

    assert response.status_code == 200
    task = Task.query.get(task.nr)
    assert task.status_id == 2

def test_delete_task(client):
    # Create a task first
    task = Task(name='Test', description='Test', status_id=1, type_id=1)
    db.session.add(task)
    db.session.commit()

    # Delete task
    response = client.post(f'/delete/{task.nr}')

    assert response.status_code == 200
    assert Task.query.get(task.nr) is None

def test_database_schema(client):
    # Test that all tables exist
    assert 'task' in db.engine.table_names()
    assert 'status' in db.engine.table_names()
    assert 'task_type' in db.engine.table_names()

