<body>
    <div class="container-fluid py-4">
        <div class="row justify-content-center">
            <div class="col-lg-10">
                <div class="glass-container">
                    <div class="row align-items-center mb-4">
                        <div class="col-md-6">
                            <h1 class="display-4 fw-bold text-white mb-0">
                                <i class="fas fa-bug text-danger me-2"></i>Todo-Liste
                            </h1>
                            <p class="text-white-50 mb-0">Verwalte Bugs & Ideen effizient</p>
                        </div>
                        <div class="col-md-6 text-md-end">
                            <button class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#addTaskModal">
                                <i class="fas fa-plus me-2"></i>Neue Aufgabe
                            </button>
                        </div>
                    </div>

                    <!-- Search Bar -->
                    <div class="search-box mb-4">
                        <i class="fas fa-search"></i>
                        <input type="text" class="form-control" id="searchInput" placeholder="Suche nach Name oder Beschreibung...">
                    </div>

                    <!-- Tasks Table -->
                    <div class="table-responsive">
                        <table class="table table-hover align-middle">
                            <thead>
                                <tr>
                                    <th class="text-white">NR</th>
                                    <th class="text-white">Name</th>
                                    <th class="text-white">Beschreibung</th>
                                    <th class="text-white">Typ</th>
                                    <th class="text-white">Status</th>
                                    <th class="text-white">Erstellt</th>
                                    <th class="text-white">Aktionen</th>
                                </tr>
                            </thead>
                            <tbody id="tasksBody">
                                {% for task in tasks %}
                                <tr class="glass-card animate__animated animate__fadeInUp">
                                    <td class="text-white fw-bold">{{ task.nr }}</td>
                                    <td class="text-white">
                                        <div class="d-flex align-items-center gap-2">
                                            {% if task.type_id == 1 %}
                                            <i class="fas fa-bug text-danger"></i>
                                            {% else %}
                                            <i class="fas fa-lightbulb text-warning"></i>
                                            {% endif %}
                                            {{ task.name }}
                                        </div>
                                    </td>
                                    <td class="text-white-50">
                                        {% if task.description %}
                                        {{ task.description|truncate(50) }}
                                        {% else %}
                                        <span class="text-muted">Keine Beschreibung</span>
                                        {% endif %}
                                    </td>
                                    <td>
                                        <span class="type-badge 
                                            {% if task.type_id == 1 %}type-bug
                                            {% else %}type-idea{% endif %}">
                                            {% if task.type_id == 1 %}Bug
                                            {% else %}Idee{% endif %}
                                        </span>
                                    </td>
                                    <td>
                                        <span class="status-badge 
                                            {% if task.status_id == 1 %}status-open
                                            {% elif task.status_id == 2 %}status-in-progress
                                            {% else %}status-done{% endif %}">
                                            {{ task.status.status_name }}
                                        </span>
                                    </td>
                                    <td class="text-white-50">{{ task.created_at.strftime('%d.%m.%Y %H:%M') }}</td>
                                    <td>
                                        <div class="btn-group" role="group">
                                            <form method="POST" action="{{ url_for('update_status', task_id=task.nr) }}" style="display: inline;">
                                                <select name="status_id" class="form-select form-select-sm" onchange="this.form.submit()" style="width: auto;">
                                                    {% for status in statuses %}
                                                    <option value="{{ status.status_id }}" 
                                                        {% if task.status_id == status.status_id %}selected{% endif %}>
                                                        {{ status.status_name }}
                                                    <<LOCAL_PATH>                                                    {% endfor %}
                                                </select>
                                            </form>
                                            <button type="button" class="btn btn-danger btn-sm" onclick="deleteTask({{ task.nr }})">
                                                <i class="fas fa-trash"></i>
                                            </button>
                                        </div>
                                    </td>
                                </tr>
                                {% endfor %}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    </div>

    <!-- Add Task Modal -->
    <div class="modal fade" id="addTaskModal" tabindex="-1">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title">Neue Aufgabe hinzufügen</h5>
                    <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
                </div>
                <form method="POST" action="{{ url_for('add_task') }}">
                    <div class="modal-body">
                        <div class="mb-3">
                            <label class="form-label">Name *</label>
                            <input type="text" class="form-control" name="name" required>
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Beschreibung</label>
                            <textarea class="form-control" name="description" rows="3"></textarea>
                        </div>
                        <div class="row">
                            <div class="col-md-6 mb-3">
                                <label class="form-label">Typ *</label>
                                <select class="form-select" name="type_id" required>
                                    <option value="">-- Wählen Sie einen Typ --<<LOCAL_PATH>                                    {% for type in types %}
                                    <option value="{{ type.type_id }}">{{ type.type_name }}<<LOCAL_PATH>                                    {% endfor %}
                                </select>
                            </div>
                            <div class="col-md-6 mb-3">
                                <label class="form-label">Status *</label>
                                <select class="form-select" name="status_id" required>
                                    <option value="">-- Wählen Sie einen Status --<<LOCAL_PATH>                                    {% for status in statuses %}
                                    <option value="{{ status.status_id }}">{{ status.status_name }}<<LOCAL_PATH>                                    {% endfor %}
                                </select>
                            </div>
                        </div>
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Abbrechen</button>
                        <button type="submit" class="btn btn-primary">Hinzufügen</button>
                    </div>
                </form>
            </div>
        </div>
    </div>

    <script src="http<LOCAL_PATH>"></script>
    <script>
        // Search functionality
        document.getElementById('searchInput').addEventListener('input', function(e) {
            const searchTerm = e.target.value.toLowerCase();
            const rows = document.querySelectorAll('#tasksBody tr');

            rows.forEach(row => {
                const text = row.textContent.toLowerCase();
                row.style.display = text.includes(searchTerm) ? '' : 'none';
            });
        });

        // Delete task functionality
        function deleteTask(taskId) {
            if (confirm('Möchten Sie diese Aufgabe wirklich löschen?')) {
                fetch(`/delete/${taskId}`, {
                    method: 'POST',
                    headers: {
                        'X-Requested-With': 'XMLHttpRequest',
                        'X-CSRF-Token': '{{ csrf_token() }}'
                    }
                })
                .then(response => response.json())
                .then(data => {
                    if (data.success) {
                        location.reload();
                    }
                })
                .catch(error => console.error('Error:', error));
            }
        }

        // Auto-hide success messages after 5 seconds
        setTimeout(function() {
            const alerts = document.querySelectorAll('.alert');
            alerts.forEach(alert => {
                const fadeEffect = setInterval(function() {
                    if (!alert.style.opacity) {
                        alert.style.opacity = 1;
                    }
                    if (alert.style.opacity > 0) {
                        alert.style.opacity -= 0.1;
                    } else {
                        clearInterval(fadeEffect);
                        alert.remove();
                    }
                }, 200);
            });
        }, 5000);
    </script>
</body>
</html>
