from flask import Flask, render_template, request, redirect, url_for, jsonify
from flask_sqlalchemy import SQLAlchemy
from datetime import datetime

app = Flask(__name__)
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlit<LOCAL_PATH>'
app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = False
db = SQLAlchemy(app)

# Lookup tables
class Status(db.Model):
    status_id = db.Column(db.Integer, primary_key=True)
    status_name = db.Column(db.String(50), unique=True, nullable=False)

class TaskType(db.Model):
    type_id = db.Column(db.Integer, primary_key=True)
    type_name = db.Column(db.String(50), unique=True, nullable=False)

# Core task table
class Task(db.Model):
    nr = db.Column(db.Integer, primary_key=True, autoincrement=True)
    name = db.Column(db.String(200), nullable=False)
    description = db.Column(db.Text)
    status_id = db.Column(db.Integer, db.ForeignKey('status.status_id'), nullable=False)
    type_id = db.Column(db.Integer, db.ForeignKey('task_type.type_id'), nullable=False)
    created_at = db.Column(db.DateTime, default=datetime.utcnow)
    updated_at = db.Column(db.DateTime, default=datetime.utcnow, onupdate=datetime.utcnow)

    status = db.relationship('Status', backref='tasks')
    task_type = db.relationship('TaskType', backref='tasks')

# Create database and seed initial data
with app.app_context():
    db.create_all()

    if not Status.query.first():
        db.session.add_all([
            Status(status_id=1, status_name='Offen'),
            Status(status_id=2, status_name='In Bearbeitung'),
            Status(status_id=3, status_name='Erledigt')
        ])

    if not TaskType.query.first():
        db.session.add_all([
            TaskType(type_id=1, type_name='Bug'),
            TaskType(type_id=2, type_name='Idee')
        ])

    db.session.commit()

@app.route('/')
def index():
    tasks = Task.query.order_by(Task.created_at.desc()).all()
    return render_template('index.html', tasks=tasks)

@app.route('/add', methods=['POST'])
def add_task():
    name = request.form.get('name')
    description = request.form.get('description')
    task_type = request.form.get('type')
    status = request.form.get('status')

