{% extends "base.html" %}

{% block head %}
<style>
    .todo-item {
        transition: all 0.3s ease;
    }
    .todo-item:hover {
        transform: translateY(-5px);
        box-shadow: 0 10px 20px rgba(0,0,0,0.1);
    }
</style>
{% endblock %}

{% block content %}
<h2>Todos</h2>

<!-- Formular zum Hinzufügen -->
<div class="glass-panel p-4 mb-4">
    <h3>Neues Todo hinzufügen</h3>
    <form action="{{ url_for('add_todo') }}" method="post" class="todo-form">
        {{ form.csrf_token }}
        <div class="row mb-3">
            <div class="col-md-3">
                <label for="nr" class="form-label">NR</label>
                <input type="number" class="form-control glass-input" id="nr" name="nr" required>
            </div>
            <div class="col-md-9">
                <label for="name" class="form-label">Name</label>
                <input type="text" class="form-control glass-input" id="name" name="name" required>
            </div>
        </div>

        <div class="mb-3">
            <label for="beschreibung" class="form-label">Beschreibung</label>
            <textarea class="form-control glass-input" id="beschreibung" name="beschreibung" rows="3"></textarea>
        </div>

        <div class="mb-3">
            <label for="status" class="form-label">Status</label>
            <select class="form-select glass-input" id="status" name="status">
                <option value="offen">Offen<<LOCAL_PATH>                <option value="in_bearbeitung">In Bearbeitung<<LOCAL_PATH>                <option value="erledigt">Erledigt<<LOCAL_PATH>            </select>
        </div>

        <button type="submit" class="btn btn-primary glass-button">Hinzufügen</button>
    </form>
</div>

<!-- Todo-Liste -->
<div class="glass-panel p-4">
    <h3>Aktuelle Todos</h3>
    <div class="table-responsive">
        <table class="table table-hover">
            <thead>
                <tr>
                    <th>NR</th>
                    <th>Name</th>
                    <th>Beschreibung</th>
                    <th>Status</th>
                    <th>Aktionen</th>
                </tr>
            </thead>
            <tbody>
                {% for todo in todos %}
                <tr class="todo-item" data-todo-id="{{ todo.id }}">
                    <td>{{ todo.nr }}</td>
                    <td>{{ todo.name }}</td>
                    <td>{{ todo.beschreibung }}</td>
                    <td>
                        <form action="{{ url_for('update_status', todo_id=todo.id) }}" method="post" class="status-form">
                            {{ form.csrf_token }}
                            <select class="form-select status-select glass-input" name="status">
                                <option value="offen" {% if todo.status == 'offen' %}selected{% endif %}>Offen<<LOCAL_PATH>                                <option value="in_bearbeitung" {% if todo.status == 'in_bearbeitung' %}selected{% endif %}>In Bearbeitung<<LOCAL_PATH>                                <option value="erledigt" {% if todo.status == 'erledigt' %}selected{% endif %}>Erledigt<<LOCAL_PATH>                            </select>
                        </form>
                    </td>
                    <td>
                        <form action="{{ url_for('delete_todo', todo_id=todo.id) }}" method="post" class="delete-form">
                            {{ form.csrf_token }}
                            <button type="submit" class="btn btn-danger btn-sm glass-button">Löschen</button>
                        </form>
                    </td>
                </tr>
                {% else %}
                <tr>
                    <td colspan="5">Keine Todos gefunden.</td>
                </tr>
                {% endfor %}
            </tbody>
        </table>
    </div>
</div>
{% endblock %}

{% block scripts %}
<script>
    // Verhindert das Neuladen der Seite beim Status-Update
    document.querySelectorAll('.status-form').forEach(form => {
        form.addEventListener('change', function(e) {
            e.preventDefault();
            const formData = new FormData(this);
            const todoId = this.closest('tr').dataset.todoId;

            fetch(`/update_status/${todoId}`, {
                method: 'POST',
                body: formData
            })
            .then(response => response.json())
            .then(data => {
                if (data.status === 'success') {
                    // Optional: Benachrichtigung anzeigen
                    console.log('Status aktualisiert:', data.message);
                } else {
                    console.error('Fehler:', data.message);
                }
            })
            .catch(error => {
                console.error('Fehler:', error);
            });
        });
    });

    // Bestätigung für Löschvorgang
    document.querySelectorAll('.delete-form').forEach(form => {
        form.addEventListener('submit', function(e) {
            if (!confirm('Soll dieses Todo wirklich gelöscht werden?')) {
                e.preventDefault();
            }
        });
    });
</script>
{% endblock %}
