document.addEventListener('DOMContentLoaded', function() {
    // Hier können wir zusätzliche JavaScript-Funktionen hinzufügen

    // Beispiel: Animation beim Laden der Seite
    const glassElements = document.querySelectorAll('.glass-panel, .glass-header');
    glassElements.forEach((element, index) => {
        element.style.opacity = '0';
        element.style.transform = 'translateY(20px)';
        setTimeout(() => {
            element.style.transition = 'all 0.5s ease';
            element.style.opacity = '1';
            element.style.transform = 'translateY(0)';
        }, index * 100);
    });
});
