// Document ready function
document.addEventListener('DOMContentLoaded', function() {
    // Formularvalidierung
    const form = document.getElementById('todo-form');
    form.addEventListener('submit', function(event) {
        event.preventDefault();
        event.stopPropagation();

        if (form.checkValidity()) {
            createTodo();
        }

        form.classList.add('was-validated');
    });

    // Toast-Benachrichtigung initialisieren
    const toastEl = document.getElementById('liveToast');
    const toast = new bootstrap.Toast(toastEl);

    // Toast-Nachricht anzeigen
    function showToast(message) {
        document.getElementById('toast-message').textContent = message;
        toast.show();
    }

    // Formular zurücksetzen nach erfolgreichem Hinzufügen
    form.addEventListener('submit', function() {
        if (form.checkValidity()) {
            setTimeout(() => {
                form.reset();
                form.classList.remove('was-validated');
            }, 1000);
        }
    });

    // Globale Funktion für Toast
    window.showToast = showToast;
});

// Funktion zum Erstellen eines neuen Todos
function createTodo() {
    const nr = document.getElementById('nr').value;
    const name = document.getElementById('name').value;
    const beschreibung = document.getElementById('beschreibung').value;
    const status = document.getElementById('status').value;

    fetch('/api/todos', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            nr: parseInt(nr),
            name: escapeHtml(name),
            beschreibung: escapeHtml(beschreibung),
            status: status
        })
    })
    .then(response => {
        if (response.ok) {
            return response.json();
        } else {
            throw new Error('Failed to create todo');
        }
    })
    .then(data => {
        // Füge das neue Todo zur Tabelle hinzu
        const tbody = document.querySelector('#todo-list tbody');
        const row = createTodoRow(data);
        tbody.appendChild(row);

        // Zeige Erfolgsmeldung an
        showToast('Todo wurde erfolgreich hinzugefügt!');

        // Formular zurücksetzen
        document.getElementById('todo-form').reset();
        document.getElementById('todo-form').classList.remove('was-validated');
    })
    .catch(error => {
        console.error('Error creating todo:', error);
        showToast('Fehler beim Erstellen des Todo: ' + error.message);
    });
}

// Funktion zum Erstellen einer Tabellenzeile für ein Todo
function createTodoRow(todo) {
    const row = document.createElement('tr');

    // Formatieren des Status
    const statusClass = `status-${todo.status}`;
    const statusText = todo.status.replace('_', ' ').title();

    row.innerHTML = `
        <td>${todo.nr}</td>
        <td>${escapeHtml(todo.name)}</td>
        <td>${escapeHtml(todo.beschreibung)}</td>
        <td><span class="status-badge ${statusClass}">${statusText}</span></td>
        <td>
            <form method="POST" action="/todo/${todo.id}/toggle-status">
                <button type="submit" class="btn btn-sm btn-outline-primary change-status-btn">
                    Status ändern
                </button>
            </form>
        </td>
    `;

    // Fade-in Animation
    row.style.opacity = '0';
    row.style.transform = 'translateY(20px)';
    setTimeout(() => {
        row.style.transition = 'opacity 0.5s ease, transform 0.5s ease';
        row.style.opacity = '1';
        row.style.transform = 'translateY(0)';
    }, 10);

    return row;
}

// Funktion zum Escapen von HTML, um XSS zu verhindern
function escapeHtml(text) {
    const map = {
        '&': '&amp;',
        '<': '&lt;',
        '>': '&gt;',
        '"': '&quot;',
        "'": '&#039;'
    };
    return text.replace(/[&<>"']/g, function(m) { return map[m]; });
}
