@app.route('/api/todos', methods=['POST'])
@csrf.exempt
def create_todo():
    data = request.get_json()

    # Validiere die Eingaben
    if not all(key in data for key in ['nr', 'name', 'beschreibung', 'status']):
        return jsonify({'error': 'Fehlende Felder'}), 400

    # Erstelle neuen Todo
    todo = Todo(
        nr=data['nr'],
        name=data['name'],
        beschreibung=data['beschreibung'],
        status=data['status']
    )

    db.session.add(todo)
    db.session.commit()

    return jsonify({
        'id': todo.id,
        'nr': todo.nr,
        'name': escape(todo.name),
        'beschreibung': escape(todo.beschreibung),
        'status': todo.status,
        'erstellt_am': todo.erstellt_am.strftime('%Y-%m-%d'),
        'aktualisiert_am': todo.aktualisiert_am.strftime('%Y-%m-%d')
    }), 201

