from datetime import datetime
from src.app import db

class Todo(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    nr = db.Column(db.Integer, nullable=False, unique=True)
    name = db.Column(db.String(100), nullable=False)
    beschreibung = db.Column(db.Text)
    status = db.Column(db.String(20), nullable=False, default='offen')
    erstellt_am = db.Column(db.DateTime, nullable=False, default=datetime.utcnow)
    aktualisiert_am = db.Column(db.DateTime, nullable=False, default=datetime.utcnow, onupdate=datetime.utcnow)

    # Beziehungen
    kommentare = db.relationship('Kommentar', backref='todo', lazy=True, cascade='all, delete-orphan')
    aenderungen = db.relationship('Aenderung', backref='todo', lazy=True, cascade='all, delete-orphan')

    def __repr__(self):
        return f'<Todo {self.nr}: {self.name}>'

class Kommentar(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    todo_id = db.Column(db.Integer, db.ForeignKey('todo.id'), nullable=False)
    kommentar = db.Column(db.Text, nullable=False)
    erstellt_am = db.Column(db.DateTime, nullable=False, default=datetime.utcnow)

    def __repr__(self):
        return f'<Kommentar {self.id}: {self.kommentar[:20]}>'

class Aenderung(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    todo_id = db.Column(db.Integer, db.ForeignKey('todo.id'), nullable=False)
    status = db.Column(db.String(20), nullable=False)
    geaendert_am = db.Column(db.DateTime, nullable=False, default=datetime.utcnow)

    def __repr__(self):
        return f'<Aenderung {self.id}: {self.todo_id} -> {self.status}>'
