document.addEventListener('DOMContentLoaded', function() {
    loadTodos();
    loadKommentare();

    document.getElementById('todo-form').addEventListener('submit', function(e) {
        e.preventDefault();
        createTodo();
    });

    document.getElementById('kommentar-form').addEventListener('submit', function(e) {
        e.preventDefault();
        createKommentar();
    });
});

function loadTodos() {
    fetch('/api/todos')
        .then(response => response.json())
        .then(todos => {
            const todoList = document.getElementById('todo-list');
            todoList.innerHTML = '';
            todos.forEach(todo => {
                const todoItem = document.createElement('li');
                todoItem.className = `status-${todo.status}`;
                todoItem.innerHTML = `
                    <span>
                        ${escapeHtml(todo.nr)} - ${escapeHtml(todo.name)}<br>
                        <small>${escapeHtml(todo.beschreibung || '')}</small>
                    </span>
                    <span class="status-badge">${todo.status.replace('_', ' ')}</span>
                `;
                todoList.appendChild(todoItem);
            });
        })
        .catch(error => console.error('Error loading todos:', error));
}

function createTodo() {
    const nr = document.getElementById('nr').value;
    const name = document.getElementById('name').value;
    const beschreibung = document.getElementById('beschreibung').value;
    const status = document.getElementById('status').value;

    fetch('/api/todos', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': getCookie('csrf_token')
        },
        body: JSON.stringify({ nr, name, beschreibung, status })
    })
    .then(response => response.json())
    .then(todo => {
        loadTodos();
        document.getElementById('todo-form').reset();
    })
    .catch(error => console.error('Error creating todo:', error));
}

function loadKommentare() {
    // Implementiere Kommentar-Laden wenn benötigt
    console.log('Kommentare geladen');
}

function createKommentar() {
    // Implementiere Kommentar-Erstellen wenn benötigt
    console.log('Kommentar erstellt');
}

function escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
}

function getCookie(name) {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
}
