from datetime import datetime
from src.app import db

class Status(db.Model):
    status_id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String(50), nullable=False, unique=True)
    colour_hex = db.Column(db.String(7), nullable=True)

class Item(db.Model):
    nr = db.Column(db.Integer, primary_key=True, autoincrement=True)
    name = db.Column(db.String(200), nullable=False)
    description = db.Column(db.Text, nullable=True)
    type = db.Column(db.String(10), nullable=False)
    status_id = db.Column(db.Integer, db.ForeignKey('status.status_id'), nullable=False)
    created_at = db.Column(db.DateTime, nullable=False, default=datetime.utcnow)
    updated_at = db.Column(db.DateTime, nullable=False, default=datetime.utcnow)

    status = db.relationship('Status', backref=db.backref('items', lazy=True))

