from flask import Blueprint, render_template, redirect, url_for, request, flash
from flask_wtf import FlaskForm
from wtforms import StringField, TextAreaField, SelectField, SubmitField
from wtforms.validators import DataRequired, Length
from datetime import datetime
from src import db
from src.models import Item, Status
from src.forms import ItemForm

bp = Blueprint('items', __name__, url_prefix='/items')

@bp.route('/')
def index():
    items = Item.query.order_by(Item.nr).all()
    return render_template('items/index.html', items=items)

@bp.route('/new', methods=['GET', 'POST'])
def new():
    form = ItemForm()
    form.status.choices = [(s.status_id, s.name) for s in Status.query.all()]

    if form.validate_on_submit():
        item = Item(
            name=form.name.data,
            description=form.description.data,
            type=form.type.data,
            status_id=form.status.data
        )
        db.session.add(item)
        db.session.commit()
        flash('Eintrag erfolgreich erstellt!', 'success')
        return redirect(url_for('items.index'))

    return render_template('items/new.html', form=form)

@bp.route('/<int:nr>/edit', methods=['GET', 'POST'])
def edit(nr):
    item = Item.query.get_or_404(nr)
    form = ItemForm(obj=item)
    form.status.choices = [(s.status_id, s.name) for s in Status.query.all()]

    if form.validate_on_submit():
        form.populate_obj(item)
        item.updated_at = datetime.utcnow()
        db.session.commit()
        flash('Eintrag erfolgreich aktualisiert!', 'success')
        return redirect(url_for('items.index'))

    return render_template('items/edit.html', form=form, item=item)

@bp.route('/<int:nr>/delete', methods=['POST'])
def delete(nr):
    item = Item.query.get_or_404(nr)
    db.session.delete(item)
    db.session.commit()
    flash('Eintrag erfolgreich gelöscht!', 'success')
    return redirect(url_for('items.index'))

