{% extends 'base.html' %}

{% block content %}
<div class="glass-container">
    <h1 class="mb-4">
        <i class="fas fa-list"></i> Alle Einträge
    </h1>

    {% if items %}
    <div class="table-responsive">
        <table class="table table-hover">
            <thead>
                <tr>
                    <th>NR</th>
                    <th>Name</th>
                    <th>Typ</th>
                    <th>Beschreibung</th>
                    <th>Status</th>
                    <th>Erstellt</th>
                    <th>Geändert</th>
                    <th>Aktionen</th>
                </tr>
            </thead>
            <tbody>
                {% for item in items %}
                <tr class="glass-card">
                    <td>{{ item.nr }}</td>
                    <td>
                        <strong>{{ item.name }}</strong>
                        {% if item.type == 'bug' %}
                            <i class="fas fa-bug text-danger ms-2"></i>
                        {% else %}
                            <i class="fas fa-lightbulb text-warning ms-2"></i>
                        {% endif %}
                    </td>
                    <td>{{ item.type|capitalize }}</td>
                    <td>{{ item.description or 'Keine Beschreibung' }}</td>
                    <td>
                        <span class="status-badge" style="background-color: {{ item.status.colour_hex }}; color: white;">
                            {{ item.status.name }}
                        </span>
                    </td>
                    <td>{{ item.created_at.strftime('%d.%m.%Y %H:%M') }}</td>
                    <td>{{ item.updated_at.strftime('%d.%m.%Y %H:%M') }}</td>
                    <td>
                        <a href="{{ url_for('items.edit', nr=item.nr) }}" class="btn btn-sm btn-glass text-primary">
                            <i class="fas fa-edit"></i> Bearbeiten
                        </a>
                        <form method="POST" action="{{ url_for('items.delete', nr=item.nr) }}" style="display: inline;">
                            <button type="submit" class="btn btn-sm btn-glass text-danger" onclick="return confirm('Wirklich löschen?')">
                                <i class="fas fa-trash"></i> Löschen
                            </button>
                        </form>
                    </td>
                </tr>
                {% endfor %}
            </tbody>
        </table>
    </div>
    {% else %}
    <div class="text-center py-5">
        <i class="fas fa-inbox fa-3x text-muted mb-3"></i>
        <h4 class="text-muted">Noch keine Einträge vorhanden</h4>
        <p class="text-secondary">Erstelle deinen ersten Bug oder deine erste Idee</p>
        <a href="{{ url_for('items.new') }}" class="btn btn-glass">
            <i class="fas fa-plus"></i> Ersten Eintrag erstellen
        </a>
    </div>
    {% endif %}
</div>
{% endblock %}

