                    <td>{{ item.updated_at.strftime('%d.%m.%Y %H:%M') }}</td>
                    <td>
                        <a href="{{ url_for('items.edit', nr=item.nr) }}" class="btn btn-sm btn-glass text-primary">
                            <i class="fas fa-edit"></i> Bearbeiten
                        </a>
                        <form method="POST" action="{{ url_for('items.delete', nr=item.nr) }}" style="display: inline;">
                            <button type="submit" class="btn btn-sm btn-glass text-danger" onclick="return confirm('Wirklich löschen?')">
                                <i class="fas fa-trash"></i> Löschen
                            </button>
                        </form>
                    </td>
                </tr>
                {% endfor %}
            </tbody>
        </table>
    </div>
    {% else %}
    <div class="text-center py-5">
        <i class="fas fa-inbox fa-3x text-muted mb-3"></i>
        <h4 class="text-muted">Noch keine Einträge vorhanden</h4>
        <p class="text-secondary">Erstelle deinen ersten Bug oder deine erste Idee</p>
        <a href="{{ url_for('items.new') }}" class="btn btn-glass">
            <i class="fas fa-plus"></i> Ersten Eintrag erstellen
        </a>
    </div>
    {% endif %}
</div>
{% endblock %}

