{"schema": "**Datenbankschema**
```sql
CREATE TABLE TodoList (
  NR INTEGER PRIMARY KEY AUTOINCREMENT,
  Name TEXT NOT NULL,
  Beschreibung TEXT,
  Status TEXT CHECK(Status IN ('Offen', 'In Bearbeitung', 'Erledigt'))
);

CREATE TABLE Bugs (
  BugID INTEGER PRIMARY KEY AUTOINCREMENT,
  TodoListNR INTEGER,
  BugName TEXT NOT NULL,
  BugBeschreibung TEXT,
  BugStatus TEXT CHECK(BugStatus IN ('Offen', 'In Bearbeitung', 'Erledigt')),
  FOREIGN KEY (TodoListNR) REFERENCES TodoList(NR) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE Ideen (
  IdeeID INTEGER PRIMARY KEY AUTOINCREMENT,
  TodoListNR INTEGER,
  IdeeName TEXT NOT NULL,
  IdeeBeschreibung TEXT,
  IdeeStatus TEXT CHECK(IdeeStatus IN ('Offen', 'In Bearbeitung', 'Erledigt')),
  FOREIGN KEY (TodoListNR) REFERENCES TodoList(NR) ON DELETE CASCADE ON UPDATE CASCADE
);
```

