const sqlite3 = require('sqlite3').verbose();
const path = require('path');

let db;

async function connect() {
  const dbPath = path.join(__dirname, '..', 'db', 'todo.db');
  db = await new Promise((resolve, reject) => {
    sqlite3.open(dbPath, (err) => {
      if (err) reject(err);
      else resolve();
    });
  });
  return db;
}

async function init() {
  const database = await connect();
  const sql = `
    CREATE TABLE IF NOT EXISTS TodoList (
      NR INTEGER PRIMARY KEY AUTOINCREMENT,
      Name TEXT NOT NULL,
      Beschreibung TEXT,
      Status TEXT CHECK(Status IN ('Offen', 'In Bearbeitung', 'Erledigt')) DEFAULT 'Offen'
    );
    CREATE TABLE IF NOT EXISTS Bugs (
      BugID INTEGER PRIMARY KEY AUTOINCREMENT,
      TodoListNR INTEGER,
      BugName TEXT NOT NULL,
      BugBeschreibung TEXT,
      BugStatus TEXT CHECK(BugStatus IN ('Offen', 'In Bearbeitung', 'Erledigt')) DEFAULT 'Offen',
      FOREIGN KEY (TodoListNR) REFERENCES TodoList(NR) ON DELETE CASCADE ON UPDATE CASCADE
    );
    CREATE TABLE IF NOT EXISTS Ideen (
      IdeeID INTEGER PRIMARY KEY AUTOINCREMENT,
      TodoListNR INTEGER,
      IdeeName TEXT NOT NULL,
      IdeeBeschreibung TEXT,
      IdeeStatus TEXT CHECK(IdeeStatus IN ('Offen', 'In Bearbeitung', 'Erledigt')) DEFAULT 'Offen',
      FOREIGN KEY (TodoListNR) REFERENCES TodoList(NR) ON DELETE CASCADE ON UPDATE CASCADE
    );
    PRAGMA foreign_keys = ON;
  `;
  await database.run(sql);
}

// Generic executor
async function execute(type, action, id, data) {
  const database = await connect();
  switch (type) {
    case 'TodoList':
      switch (action) {
        case 'create':
          const createTodo = await database.run(
            'INSERT INTO TodoList (Name, Beschreibung) VALUES (?, ?)',
            [data.Name, data.Beschreibung]
          );
          const todoRow = await database.get('SELECT * FROM TodoList WHERE NR = ?', createTodo.lastID);
          return todoRow;
        case 'updateStatus':
          await database.run('UPDATE TodoList SET Status = ? WHERE NR = ?', [data.Status, id]);
          const todoUpd = await database.get('SELECT * FROM TodoList WHERE NR = ?', id);
          return todoUpd;
        case 'delete':
          const todoDel = await database.get('SELECT * FROM TodoList WHERE NR = ?', id);
          await database.run('DELETE FROM TodoList WHERE NR = ?', id);
          return todoDel;
        case 'readAll':
          return await database.all('SELECT * FROM TodoList');
        defaul<LOCAL_PATH>          return null;
      }
    case 'Bug':
      switch (action) {
        case 'create':
          const createBug = await database.run(
            'INSERT INTO Bugs (BugName, BugBeschreibung, TodoListNR, BugStatus) VALUES (?, ?, ?, ?)',
            [data.BugName, data.BugBeschreibung, data.TodoListNR, data.BugStatus]
          );
          const bugRow = await database.get('SELECT * FROM Bugs WHERE BugID = ?', createBug.lastID);
          return bugRow;
        case 'updateStatus':
          await database.run('UPDATE Bugs SET BugStatus = ? WHERE BugID = ?', [data.BugStatus, id]);
          const bugUpd = await database.get('SELECT * FROM Bugs WHERE BugID = ?', id);
          return bugUpd;
        case 'delete':
          const bugDel = await database.get('SELECT * FROM Bugs WHERE BugID = ?', id);
          await database.run('DELETE FROM Bugs WHERE BugID = ?', id);
          return bugDel;
        case 'readAll':
          return await database.all('SELECT * FROM Bugs');
        defaul<LOCAL_PATH>          return null;
      }
    case 'Idee':
      switch (action) {
        case 'create':
          const createIdee = await database.run(
            'INSERT INTO Ideen (IdeeName, IdeeBeschreibung, TodoListNR, IdeeStatus) VALUES (?, ?, ?, ?)',
            [data.IdeeName, data.IdeeBeschreibung, data.TodoListNR, data.IdeeStatus]
          );
          const ideeRow = await database.get('SELECT * FROM Ideen WHERE IdeeID = ?', createIdee.lastID);
          return ideeRow;
        case 'updateStatus':
          await database.run('UPDATE Ideen SET IdeeStatus = ? WHERE IdeeID = ?', [data.IdeeStatus, id]);
          const ideeUpd = await database.get('SELECT * FROM Ideen WHERE IdeeID = ?', id);
          return ideeUpd;
        case 'delete':
          const ideeDel = await database.get('SELECT * FROM Ideen WHERE IdeeID = ?', id);
          await database.run('DELETE FROM Ideen WHERE IdeeID = ?', id);
          return ideeDel;
        case 'readAll':
