import { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import Glassmorphism from '../components/Glassmorphism';
import StatusSelect from '../components/StatusSelect';
import TodoForm from '../components/TodoForm';
import BugForm from '../components/BugForm';
import IdeeForm from '../components/IdeaForm';

export default function Home() {
  const [todos, setTodos] = useState([]);
  const [bugs, setBugs] = useState([]);
  const [ideas, setIdeas] = useState([]);
  const [newTodo, setNewTodo] = useState({ Name: '', Beschreibung: '' });
  const [newBug, setNewBug] = useState({ BugName: '', BugBeschreibung: '', TodoListNR: '', BugStatus: 'Offen' });
  const [newIdea, setNewIdea] = useState({ IdeeName: '', IdeeBeschreibung: '', TodoListNR: '', IdeeStatus: 'Offen' });

  const fetchData = async () => {
    const dataTodos = await fetch('/api/data?type=TodoList&action=readAll').then(r => r.json());
    const dataBugs = await fetch('/api/data?type=Bug&action=readAll').then(r => r.json());
    const dataIdeas = await fetch('/api/data?type=Idee&action=readAll').then(r => r.json());
    setTodos(dataTodos);
    setBugs(dataBugs);
    setIdeas(dataIdeas);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleTodoSubmit = async (e) => {
    e.preventDefault();
    await fetch('/api/data?type=TodoList&action=create', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newTodo)
    });
    fetchData();
    setNewTodo({ Name: '', Beschreibung: '' });
  };

  const handleBugSubmit = async (e) => {
    e.preventDefault();
    await fetch('/api/data?type=Bug&action=create', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newBug)
    });
    fetchData();
    setNewBug({ BugName: '', BugBeschreibung: '', TodoListNR: '', BugStatus: 'Offen' });
  };

  const handleIdeaSubmit = async (e) => {
    e.preventDefault();
    await fetch('/api/data?type=Idee&action=create', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newIdea)
    });
    fetchData();
    setNewIdea({ IdeeName: '', IdeeBeschreibung: '', TodoListNR: '', IdeeStatus: 'Offen' });
  };

  const handleStatusChange = async (type, id, status) => {
    await fetch(`/api/data?type=${type}&action=updateStatus&id=${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ Status: status })
    });
    fetchData();
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <Glassmorphism className="max-w-4xl w-full p-6 bg-white/30 backdrop-blur-lg rounded-lg shadow-xl">
        <h1 className="text-3xl font-bold text-center mb-6">Todo List App</h1>
        <TodoForm
          onSubmit={handleTodoSubmit}
          initialData={newTodo}
          setData={setNewTodo}
        />
        <div className="mt-6 flex flex-col md:flex-row gap-4">
          <div className="flex-1">
            <h2 className="text-xl font-semibold mb-2">Bugs</h2>
            <BugForm
              onSubmit={handleBugSubmit}
              onStatusChange={(id, status) => handleStatusChange('Bug', id, status)}
              initialData={newBug}
              setData={setNewBug}
              items={bugs}
            />
          </div>
          <div className="flex-1">
            <h2 className="text-xl font-semibold mb-2">Ideen</h2>
            <IdeaForm
              onSubmit={handleIdeaSubmit}
              onStatusChange={(id, status) => handleStatusChange('Idee', id, status)}
              initialData={newIdea}
              setData={setNewIdea}
              items={ideas}
            />
          </div>
        </div>
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">Todo Einträge</h2>
          <div className="mt-2">
            {todos.map((t) => (
              <div key={t.NR} className="flex items-center justify-between py-1 border-b border-gray-200 last:border-b-0">
                <span className="flex-1">{t.Name}</span>
                <StatusSelect value={t.Status} onChange={(e) => handleStatusChange('TodoList', t.NR, e.target.value)} />
              </div>
            ))}
          </div>
        </div>
      </Glassmorphism>
      <ToastContainer position="top-right" />
    </div>
  );
}
```

