export default function TodoForm({ onSubmit, initialData, setData }) {
  const [name, setName] = useState(initialData.Name);
  const [beschreibung, setBeschreibung] = useState(initialData.Beschreibung);

  const handleChange = (e) => {
    if (e.target.name === 'Name') setName(e.target.value);
    if (e.target.name === 'Beschreibung') setBeschreibung(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
    setData({ Name: '', Beschreibung: '' });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-2 glassmorphism bg-white/20 rounded px-3 py-2 mb-4">
      <input
        type="text"
        name="Name"
        placeholder="Name"
        value={name}
        onChange={handleChange}
        className="w-full bg-white/30 border border-white/30 rounded px-2 py-1 text-black"
      />
      <textarea
        name="Beschreibung"
        placeholder="Beschreibung"
        value={beschreibung}
        onChange={handleChange}
        className="w-full bg-white/30 border border-white/30 rounded px-2 py-1 text-black"
      />
      <button
        type="submit"
        className="w-full bg-blue-600 hover:bg-blue-700 rounded py-1 text-white"
      >
        Erstellen
      </button>
    </form>
  );
}
