import { init, execute } from '../lib/db';

export default async function handler(req, res) {
  const { type, action, id } = req.query;
  const body = req.method === 'POST' ? req.body : {};
  await init();
  let result;
  if (action === 'create') {
    result = await execute(type, 'create', null, body);
  } else if (action === 'updateStatus') {
    result = await execute(type, 'updateStatus', id, body);
  } else if (action === 'delete') {
    result = await execute(type, 'delete', id, {});
  } else if (action === 'readAll') {
    result = await execute(type, 'readAll', null, {});
  } else {
    return res.status(400).json({ error: 'Invalid request' });
  }
  if (!result) {
    return res.status(400).json({ error: 'Invalid request' });
  }
  res.status(200).json(result);
}
