const api = require('../utils/api');

jest.mock('node-fetch', () => jest.fn());

const fetchMock = require('node-fetch').mock;

const baseUrl = 'htt<LOCAL_PATH>';

describe('API Utility', () => {
  afterEach(() => {
    fetchMock.mockReset();
  });

  test('fetchTasks returns parsed JSON', async () => {
    fetchMock.mockResolvedValueOnce({
      ok: true,
      json: async () => [{ id: 1, text: 'Task 1', completed: false }],
    });

    const tasks = await api.fetchTasks();
    expect(tasks).toEqual([{ id: 1, text: 'Task 1', completed: false }]);
    expect(fetchMock).toHaveBeenCalledWith(`${baseUrl}/tasks`);
  });

  test('fetchTasks propagates network errors', async () => {
    fetchMock.mockRejectedValueOnce(new Error('Network error'));

    await expect(api.fetchTasks()).rejects.toThrow('Network error');
    expect(fetchMock).toHaveBeenCalledWith(`${baseUrl}/tasks`);
  });

  test('createTask sends POST request with new task', async () => {
    const newTask = { text: 'New task', completed: false };
    fetchMock.mockResolvedValueOnce({
      ok: true,
      json: async () => { throw new Error('should not be called'); },
    });

    await api.createTask(newTask.text);
    expect(fetchMock).toHaveBeenCalledWith(`${baseUrl}/tasks`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ text: newTask.text }),
    });
  });

  test('deleteTask sends DELETE request', async () => {
    const id = 42;
    fetchMock.mockResolvedValueOnce({ ok: true });

    await api.deleteTask(id);
    expect(fetchMock).toHaveBeenCalledWith(`${baseUrl}/tasks/${id}`, {
      method: 'DELETE',
    });
  });

  test('toggleTask sends PUT request with updated completion', async () => {
    const id = 7;
    const updated = { completed: true };
    fetchMock.mockResolvedValueOnce({ ok: true });

    await api.toggleTask(id, updated);
    expect(fetchMock).toHaveBeenCalledWith(`${baseUrl}/tasks/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated),
    });
  });
});
