// tests/test_db.js
// SQLite database tests using Jest and sqlite3

const path = require('path');
const sqlite = require('sqlite3');
const { open } = require('sqlite');

let db;

// Connect to an in‑memory SQLite database for tests
beforeAll(async () => {
  db = await open({
    filename: path.join(__dirname, '../db/test.db'),
    driver: sqlite.Database,
  });

  // Create a simple table for testing
  await db.exec(`
    CREATE TABLE IF NOT EXISTS users (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      username TEXT NOT NULL,
      email TEXT NOT NULL,
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP
    );
  `);
});

// Close the database after all tests
afterAll(async () => {
  await db.close();
});

// Clean up data before each test
beforeEach(async () => {
  await db.run(`DELETE FROM users`);
});

// Insert a user and return the newly created record
async function insertUser(username, email) {
  const result = await db.run(
    `INSERT INTO users (username, email) VALUES (?, ?);`,
    username,
    email
  );
  return await db.get(`SELECT * FROM users WHERE id = ?;`, result.lastID);
}

// -------------------------------------------------------------------
// Test suite
// -------------------------------------------------------------------
describe('SQLite database operations', () => {
  test('creates users table successfully', async () => {
    const row = await db.get(`PRAGMA table_info(users);`);
    expect(row).toBeDefined();
    expect(row.length).toBeGreaterThan(0);
  });

  test('inserts a new user and returns it', async () => {
    const user = await insertUser('john_doe', 'john@example.com');
    expect(user.id).toBeGreaterThan(0);
    expect(user.username).toBe('john_doe');
    expect(user.email).toBe('john@example.com');
  });

  test('retrieves a user by id', async () => {
    const created = await insertUser('alice', 'alice@example.com');
    const retrieved = await db.get(`SELECT * FROM users WHERE id = ?;`, created.id);
    expect(retrieved.username).toBe('alice');
    expect(retrieved.email).toBe('alice@example.com');
  });

  test('updates a user\'s email by id', async () => {
    const created = await insertUser('bob', 'bob@old.com');
    await db.run(`UPDATE users SET email = ? WHERE id = ?;`, 'bob@new.com', created.id);
    const updated = await db.get(`SELECT * FROM users WHERE id = ?;`, created.id);
    expect(updated.email).toBe('bob@new.com');
  });

  test('deletes a user by id', async () => {
    const created = await insertUser('charlie', 'charlie@example.com');
    const deleteResult = await db.run(`DELETE FROM users WHERE id = ?;`, created.id);
    expect(deleteResult.changes).toBe(1);
    const remaining = await db.get(`SELECT * FROM users WHERE id = ?;`, created.id);
    expect(remaining).toBeUndefined();
  });

  test('lists all users ordered by creation', async () => {
    // Insert multiple users
    await db.run(`INSERT INTO users (username, email) VALUES (?, ?);`, 'first', 'first@ex.com');
    await db.run(`INSERT INTO users (username, email) VALUES (?, ?);`, 'second', 'second@ex.com');

    const all = await db.all(`SELECT id, username, email FROM users ORDER BY created_at;`);
    expect(all).toHaveLength(2);
    expect(all[0].username).toBe('first');
    expect(all[1].username).toBe('second');
  });
});

// -------------------------------------------------------------------
// Helper function for direct query testing (exported for reuse)
// -------------------------------------------------------------------
async function queryUsersByUsername(username) {
  return await db.all(
    `SELECT id, username, email, created_at FROM users WHERE username = ?;`,
    username
  );
}

// Export helper for other test files if needed
module.exports = {
  queryUsersByUsername,
};
