import { getDb } from '../../lib/db';

export default async function handler(req, res) {
  const db = await getDb();
  try {
    if (req.method === 'GET') {
      const todos = await db.all('SELECT * FROM todos ORDER BY nr');
      res.status(200).json(todos);
    } else if (req.method === 'POST') {
      const { name, beschreibung, status } = req.body;

      if (!name || !status) {
        return res.status(400).json({ error: 'Name und Status sind erforderlich' });
      }

      const validStatuses = ['offen', 'in Arbeit', 'erledigt'];
      if (!validStatuses.includes(status)) {
        return res.status(400).json({ error: 'Ungültiger Status' });
      }

      const maxNrRow = await db.get('SELECT MAX(nr) as max_nr FROM todos');
      const nr = (maxNrRow.max_nr || 0) + 1;

      const result = await db.run(
        'INSERT INTO todos (nr, name, beschreibung, status) VALUES (?, ?, ?, ?)',
        nr, name, beschreibung, status
      );

      const newTodo = await db.get('SELECT * FROM todos WHERE id = ?', result.id);
      res.status(201).json(newTodo);
    } else {
      res.setHeader('Allow', ['GET', 'POST']);
      res.status(405).end(`Method ${req.method} Not Allowed`);
    }
  } finally {
    await db.close();
  }
}

