import handler from '../../pages/api/todos';
import { getDb } from '../../lib/db';

let db;

beforeAll(async () => {
  db = await getDb(true);
  await db.exec('DROP TABLE IF EXISTS todos');
  await db.exec(`
    CREATE TABLE IF NOT EXISTS todos (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      nr INTEGER NOT NULL,
      name TEXT NOT NULL,
      beschreibung TEXT,
      status TEXT NOT NULL CHECK(status IN ('offen', 'in Arbeit', 'erledigt'))
    );
  `);
});

afterAll(async () => {
  await db.close();
});

beforeEach(async () => {
  await db.exec('DELETE FROM todos');
});

describe('GET /api/todos', () => {
  it('should return empty array initially', async () => {
    const req = { method: 'GET' };
    const res = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn().mockReturnThis(),
    };

    await handler(req, res);

    expect(res.status).toHaveBeenCalledWith(200);
    expect(res.json).toHaveBeenCalledWith([]);
  });

  it('should return todos', async () => {
    await db.run(
      'INSERT INTO todos (nr, name, beschreibung, status) VALUES (?, ?, ?, ?)',
      1, 'Test', 'Test Beschreibung', 'offen'
    );

    const req = { method: 'GET' };
    const res = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn().mockReturnThis(),
    };

    await handler(req, res);

    expect(res.status).toHaveBeenCalledWith(200);
    expect(res.json).toHaveBeenCalledWith(
      expect.arrayContaining([
        expect.objectContaining({
          name: 'Test',
          status: 'offen',
          nr: 1
        })
      ])
    );
  });
});

describe('POST /api/todos', () => {
  it('should create a new todo', async () => {
    const newTodo = {
      name: 'Neuer Todo',
      beschreibung: 'Beschreibung',
      status: 'offen'
    };

    const req = {
      method: 'POST',
      body: JSON.stringify(newTodo)
    };
    const res = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn().mockReturnThis(),
    };

    await handler(req, res);

    expect(res.status).toHaveBeenCalledWith(201);
    expect(res.json).toHaveBeenCalledWith(
      expect.objectContaining({
        name: 'Neuer Todo',
        beschreibung: 'Beschreibung',
        status: 'offen',
        nr: 1
      })
    );
  });

  it('should return 400 for missing required fields', async () => {
    const newTodo = {
      name: '',
      status: 'offen'
    };

    const req = {
      method: 'POST',
      body: JSON.stringify(newTodo)
    };
    const res = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn().mockReturnThis(),
    };

    await handler(req, res);

    expect(res.status).toHaveBeenCalledWith(400);
    expect(res.json).toHaveBeenCalledWith({ error: 'Name und Status sind erforderlich' });
  });

  it('should return 400 for invalid status', async () => {
    const newTodo = {
      name: 'Test',
      status: 'invalid'
    };

    const req = {
      method: 'POST',
      body: JSON.stringify(newTodo)
    };
    const res = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn().mockReturnThis(),
    };

