import React, { useState, useEffect } from 'react';
import GlassCard from './GlassCard';
import { Plus, Trash2 } from 'lucide-react';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [form, setForm] = useState({
    name: '',
    beschreibung: '',
    status: 'offen'
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadTodos();
  }, []);

  const loadTodos = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/todos');
      if (response.ok) {
        const data = await response.json();
        setTodos(data);
        setError(null);
      } else {
        setError('Fehler beim Laden der Todos');
      }
    } catch (err) {
      setError('Netzwerkfehler');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name.trim()) return;

    try {
      const response = await fetch('/api/todos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });

      if (response.ok) {
        const newTodo = await response.json();
        setTodos([...todos, newTodo]);
        setForm({ name: '', beschreibung: '', status: 'offen' });
        setError(null);
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Fehler beim Erstellen');
      }
    } catch (err) {
      setError('Netzwerkfehler');
    }
  };

  const handleUpdate = async (id, updates) => {
    try {
      const response = await fetch(`/api/todos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updates)
      });

      if (response.ok) {
        const updatedTodo = await response.json();
        setTodos(todos.map(todo => todo.id === id ? updatedTodo : todo));
        setError(null);
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Fehler beim Aktualisieren');
      }
    } catch (err) {
      setError('Netzwerkfehler');
    }
  };

  const handleDelete = async (id) => {
    if (!confirm('Eintrag wirklich löschen?')) return;

    try {
      const response = await fetch(`/api/todos/${id}`, { method: 'DELETE' });
      if (response.ok) {
        setTodos(todos.filter(todo => todo.id !== id));
        setError(null);
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Fehler beim Löschen');
      }
    } catch (err) {
      setError('Netzwerkfehler');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'offen': return 'bg-yellow-100 text-yellow-800';
      case 'in Arbeit': return 'bg-blue-100 text-blue-800';
      case 'erledigt': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="w-full max-w-4xl mx-auto">
      <GlassCard className="p-8 mb-8">
        <h1 className="text-4xl font-bold text-white mb-2 text-center tracking-tight">
          Todo-Liste
        </h1>
        <p className="text-white text-opacity-80 text-center mb-8">
          Erfasse Bugs und Ideen - behalte den Überblick
        </p>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-white mb-2">
                Name <span className="text-red-400">*</span>
              </label>
              <input
                type="text"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                className="w-full p-3 border border-gray-200 border-opacity-30 rounded-xl bg-white bg-opacity-50 focus:ring-2 focus:ring-blue-400 focus:border-transparent shadow-sm"
                placeholder="Kurzer Titel"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-white mb-2">
                Status
              </label>
              <select
                value={form.status}
                onChange={(e) => setForm({ ...form, status: e.target.value })}
                className="w-full p-3 border border-gray-200 border-opacity-30 rounded-xl bg-white bg-opacity-50 focus:ring-2 focus:ring-blue-400 focus:border-transparent shadow-sm"
              >
                <option value="offen">Offen<<LOCAL_PATH>                <option value="in Arbeit">In Arbeit<<LOCAL_PATH>                <option value="erledigt">Erledigt<<LOCAL_PATH>              </select>
            </div>
            <div className="flex items-end">
              <button
                type="submit"
                className="w-full flex items-center justify-center gap-2 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-semibold py-3 px-6 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-[1.02]"
              >
                <Plus size={20} />
                Eintrag hinzufügen
              </button>
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-white mb-2">
              Beschreibung
            </label>
            <textarea
              value={form.beschreibung}
              onChange={(e) => setForm({ ...form, beschreibung: e.target.value })}
              className="w-full p-3 border border-gray-200 border-opacity-30 rounded-xl bg-white bg-opacity-50 focus:ring-2 focus:ring-blue-400 focus:border-transparent shadow-sm"
              placeholder="Detaillierte Beschreibung des Bugs oder der Idee..."
              rows="3"
            />
          </div>
        </form>

        {error && (
          <div className="mt-4 p-4 bg-red-100 border border-red-200 text-red-700 rounded-xl shadow-sm">
            {error}
          </div>
        )}
      </GlassCard>

      {loading ? (
        <GlassCard className="p-12 text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mb-4"></div>
          <p className="text-white text-opacity-80">Lade Einträge...</p>
        </GlassCard>
      ) : todos.length === 0 ? (
        <GlassCard className="p-12 text-center">
          <p className="text-white text-opacity-80 text-lg">
            Keine Einträge vorhanden. Füge den ersten hinzu!
          </p>
        </GlassCard>
      ) : (
        <div className="space-y-4">
          {todos.map((todo) => (
            <GlassCard key={todo.id} className="p-6 hover:shadow-2xl transition-all duration-300 transform hover:scale-[1.01]">
              <div className="flex flex-col md:flex-row justify-between gap-4">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <span className="text-2xl font-bold text-blue-600 min-w-[2rem]">
                      {todo.nr}.
                    </span>
                    <h3 className="text-xl font-semibold text-white">
                      {todo.name}
                    </h3>
                  </div>
                  <p className="text-white text-opacity-90 mb-4 leading-relaxed">
                    {todo.beschreibung || 'Keine Beschreibung angegeben.'}
                  </p>
                  <div className="flex items-center gap-2">
                    <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(todo.status)}`}>
                      {todo.status}
                    </span>
                    <span className="text-white text-opacity-60 text-sm">
                      ID: {todo.id}
                    </span>
                  </div>
                </div>
                <div className="flex md:flex-col items-center md:items-end gap-3">
                  <div className="w-full md:w-auto">
                    <label className="block text-xs font-medium text-white text-opacity-70 mb-1">
                      Status ändern
                    </label>
                    <select
                      value={todo.status}
                      onChange={(e) => handleUpdate(todo.id, { status: e.target.value })}
                      className="p-2 border border-gray-200 border-opacity-30 rounded-lg bg-white bg-opacity-50 focus:ring-2 focus:ring-blue-400 text-sm shadow-sm min-w-[140px]"
                    >
                      <option value="offen">Offen<<LOCAL_PATH>                      <option value="in Arbeit">In Arbeit<<LOCAL_PATH>                      <option value="erledigt">Erledigt<<LOCAL_PATH>                    </select>
                  </div>
                  <button
                    onClick={() => handleDelete(todo.id)}
                    className="flex items-center gap-2 px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-lg shadow-md hover:shadow-lg transition-all duration-300"
                  >
                    <Trash2 size={16} />
                    <span>Löschen</span>
                  </button>
                </div>
              </div>
            </GlassCard>
          ))}
        </div>
      )}

      <GlassCard className="mt-8 p-6 text-center">
        <div className="text-white text-opacity-70 space-y-1">
