import { getDb } from '../../../lib/db';

export default async function handler(req, res) {
  const { id } = req.query;
  const db = await getDb();
  try {
    if (req.method === 'GET') {
      const todo = await db.get('SELECT * FROM todos WHERE id = ?', id);
      if (!todo) {
        return res.status(404).json({ error: 'Todo nicht gefunden' });
      }
      res.status(200).json(todo);
    } else if (req.method === 'PUT') {
      const updates = req.body;
      const allowedUpdates = ['name', 'beschreibung', 'status'];

      // Reject any unexpected fields
      const invalidKeys = Object.keys(updates).filter((k) => !allowedUpdates.includes(k));
      if (invalidKeys.length > 0) {
        return res.status(400).json({ error: `Ungültige Felder: ${invalidKeys.join(', ')}` });
      }

      // Ensure at least one allowed field is present
      if (Object.keys(updates).length === 0) {
        return res.status(400).json({ error: 'Keine Felder zum Aktualisieren angegeben' });
      }

      // Validate status if present
      if (updates.status) {
        const validStatuses = ['offen', 'in Arbeit', 'erledigt'];
        if (!validStatuses.includes(updates.status)) {
          return res.status(400).json({ error: 'Ungültiger Status' });
        }
      }

      const existing = await db.get('SELECT * FROM todos WHERE id = ?', id);
      if (!existing) {
        return res.status(404).json({ error: 'Todo nicht gefunden' });
      }

      // Build SET clause from allowed fields (now safe)
      const fields = Object.keys(updates);
      const setClause = fields.map((key) => `${key} = ?`).join(', ');
      const values = fields.map((key) => updates[key]);

      const result = await db.run(
        `UPDATE todos SET ${setClause} WHERE id = ?`,
        [...values, id]
      );

      if (result.changes === 0) {
        return res.status(404).json({ error: 'Todo nicht gefunden' });
      }

      const updatedTodo = await db.get('SELECT * FROM todos WHERE id = ?', id);
      res.status(200).json(updatedTodo);
    } else if (req.method === 'DELETE') {
      const result = await db.run('DELETE FROM todos WHERE id = ?', id);
      if (result.changes === 0) {
        return res.status(404).json({ error: 'Todo nicht gefunden' });
      }
      res.status(204).end();
    } else {
      res.setHeader('Allow', ['GET', 'PUT', 'DELETE']);
      res.status(405).end(`Method ${req.method} Not Allowed`);
    }
  } finally {
    await db.close();
  }
