import { cn } from '@/lib/utils';

const statusColors = {
  open: 'bg-rose-500/20 text-rose-300 border-rose-500/30',
  in_progress: 'bg-amber-500/20 text-amber-300 border-amber-500/30',
  resolved: 'bg-emerald-500/20 text-emerald-300 border-emerald-500/30',
  closed: 'bg-slate-500/20 text-slate-300 border-slate-500/30',
  new: 'bg-cyan-500/20 text-cyan-300 border-cyan-500/30',
  reviewed: 'bg-blue-500/20 text-blue-300 border-blue-500/30',
  implemented: 'bg-emerald-500/20 text-emerald-300 border-emerald-500/30',
  rejected: 'bg-slate-500/20 text-slate-300 border-slate-500/30',
};

export function Badge({ className, status, children, ...props }) {
  const statusClass = status ? statusColors[status] : '';

  return (
    <div
      className={cn(
        'inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2',
        statusClass,
        className
      )}
      {...props}
    >
      {children}
    </div>
  );
}

