'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select } from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { cn } from '@/lib/utils';

export function CreateItemForm({ type, onSuccess }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState(type === 'bug' ? 'open' : 'new');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name.trim()) return;

    setLoading(true);
    try {
      const endpoint = type === 'bug' ? '/api/bugs' : '/api/ideas';
      const res = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, description, status }),
      });

      if (res.ok) {
        setName('');
        setDescription('');
        setStatus(type === 'bug' ? 'open' : 'new');
        onSuccess?.();
      }
    } catch (error) {
      console.error('Failed to create item:', error);
    } finally {
      setLoading(false);
    }
  };

  const statusOptions = type === 'bug' 
    ? [
        { value: 'open', label: 'Offen' },
        { value: 'in_progress', label: 'In Bearbeitung' },
        { value: 'resolved', label: 'Behoben' },
        { value: 'closed', label: 'Geschlossen' },
      ]
    : [
        { value: 'new', label: 'Neu' },
        { value: 'reviewed', label: 'Geprueft' },
        { value: 'implemented', label: 'Umgesetzt' },
        { value: 'rejected', label: 'Abgelehnt' },
      ];

  return (
    <Card className="border-white/10 bg-slate-900/60 backdrop-blur-xl sticky top-24">
      <CardHeader>
        <CardTitle className="text-base">
          {type === 'bug' ? 'Neuer Bug' : 'Neue Idee'}
        </CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <label className="text-sm font-medium text-slate-300">Name</label>
            <Input
              placeholder={type === 'bug' ? 'Bug beschreiben...' : 'Idee beschreiben...'}
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium text-slate-300">Beschreibung</label>
            <textarea
              className={cn(
                'flex min-h-[80px] w-full rounded-lg border border-white/10 bg-slate-900/60 px-3 py-2 text-sm text-white placeholder:text-slate-500 focus:outline-none focus:ring-2 focus:ring-cyan-500/50 focus:border-transparent backdrop-blur-sm resize-none'
              )}
              placeholder="Details eingeben..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium text-slate-300">Status</label>
            <Select value={status} onChange={(e) => setStatus(e.target.value)}>
              {statusOptions.map((opt) => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                <<LOCAL_PATH>              ))}
            </Select>
          </div>

          <Button type="submit" className="w-full" disabled={loading || !name.trim()}>
            {loading ? 'Wird erstellt...' : 'Erstellen'}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

