import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { getAllBugs, getAllIdeas } from '@/lib/db';
import { FaBug, FaLightbulb, FaCheckCircle, FaClock } from 'react-icons/fa';

export const dynamic = 'force-dynamic';

export default async function DashboardPage() {
  const [bugs, ideas] = await Promise.all([getAllBugs(), getAllIdeas()]);

  const openBugs = bugs.filter(b => b.status === 'open' || b.status === 'in_progress').length;
  const resolvedBugs = bugs.filter(b => b.status === 'resolved' || b.status === 'closed').length;
  const newIdeas = ideas.filter(i => i.status === 'new').length;
  const implementedIdeas = ideas.filter(i => i.status === 'implemented').length;

  const stats = [
    { title: 'Offene Bugs', value: openBugs, icon: FaBug, color: 'text-rose-400', bg: 'bg-rose-500/10' },
    { title: 'Behobene Bugs', value: resolvedBugs, icon: FaCheckCircle, color: 'text-emerald-400', bg: 'bg-emerald-500/10' },
    { title: 'Neue Ideen', value: newIdeas, icon: FaLightbulb, color: 'text-cyan-400', bg: 'bg-cyan-500/10' },
    { title: 'Umgesetzte Ideen', value: implementedIdeas, icon: FaClock, color: 'text-amber-400', bg: 'bg-amber-500/10' },
  ];

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold text-white mb-2">Dashboard</h1>
        <p className="text-slate-400">Uebersicht aller Bugs und Ideen</p>
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        {stats.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} className="border-white/10 bg-slate-900/60 backdrop-blur-xl">
              <CardHeader className="flex flex-row items-center justify-between pb-2">
                <CardTitle className="text-sm font-medium text-slate-400">
                  {stat.title}
                </CardTitle>
                <div className={`${stat.bg} p-2 rounded-lg`}>
                  <Icon className={`h-4 w-4 ${stat.color}`} />
                </div>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-white">{stat.value}</div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      <div className="grid gap-8 md:grid-cols-2">
        <Card className="border-white/10 bg-slate-900/60 backdrop-blur-xl">
          <CardHeader>
            <CardTitle className="text-lg">Letzte Bugs</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {bugs.slice(0, 5).map((bug) => (
              <div key={bug.id} className="flex items-center justify-between border-b border-white/5 pb-3 last:border-0 last:pb-0">
                <div>
                  <div className="text-sm font-medium text-white">{bug.name}</div>
                  <div className="text-xs text-slate-500">{bug.nr}</div>
                </div>
                <div className={`text-xs px-2 py-1 rounded-full ${
                  bug.status === 'open' ? 'bg-rose-500/20 text-rose-300' :
                  bug.status === 'in_progress' ? 'bg-amber-500/20 text-amber-300' :
                  'bg-emerald-500/20 text-emerald-300'
                }`}>
                  {bug.status === 'open' ? 'Offen' : 
                   bug.status === 'in_progress' ? 'In Bearbeitung' : 
                   bug.status === 'resolved' ? 'Behoben' : 'Geschlossen'}
                </div>
              </div>
            ))}
            {bugs.length === 0 && <div className="text-sm text-slate-500">Keine Bugs vorhanden</div>}
          </CardContent>
        </Card>

        <Card className="border-white/10 bg-slate-900/60 backdrop-blur-xl">
          <CardHeader>
            <CardTitle className="text-lg">Letzte Ideen</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {ideas.slice(0, 5).map((idea) => (
              <div key={idea.id} className="flex items-center justify-between border-b border-white/5 pb-3 last:border-0 last:pb-0">
                <div>
                  <div className="text-sm font-medium text-white">{idea.name}</div>
                  <div className="text-xs text-slate-500">{idea.nr}</div>
                </div>
                <div className={`text-xs px-2 py-1 rounded-full ${
                  idea.status === 'new' ? 'bg-cyan-500/20 text-cyan-300' :
                  idea.status === 'reviewed' ? 'bg-blue-500/20 text-blue-300' :
                  idea.status === 'implemented' ? 'bg-emerald-500/20 text-emerald-300' :
                  'bg-slate-500/20 text-slate-300'
                }`}>
                  {idea.status === 'new' ? 'Neu' : 
                   idea.status === 'reviewed' ? 'Geprueft' : 
                   idea.status === 'implemented' ? 'Umgesetzt' : 'Abgelehnt'}
                </div>
              </div>
            ))}
            {ideas.length === 0 && <div className="text-sm text-slate-500">Keine Ideen vorhanden</div>}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

