'use client';

import { useEffect, useState } from 'react';
import { BugCard } from '@/components/bug-card';
import { CreateItemForm } from '@/components/create-item-form';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';

export default function BugsPage() {
  const [bugs, setBugs] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchBugs = async () => {
    try {
      const res = await fetch('/api/bugs');
      const data = await res.json();
      setBugs(data);
    } catch (error) {
      console.error('Failed to fetch bugs:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBugs();
  }, []);

  return (
    <div className="grid gap-8 lg:grid-cols-3">
      <div className="lg:col-span-1">
        <CreateItemForm type="bug" onSuccess={fetchBugs} />
      </div>

      <div className="lg:col-span-2 space-y-4">
        <Card className="border-white/10 bg-slate-900/60 backdrop-blur-xl">
          <CardHeader>
            <CardTitle className="text-lg">Bugs ({bugs.length})</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {loading ? (
              <div className="text-center py-8 text-slate-500">Laden...</div>
            ) : bugs.length === 0 ? (
              <div className="text-center py-8 text-slate-500">
                Keine Bugs vorhanden. Erstellen Sie einen neuen Bug.
              </div>
            ) : (
              bugs.map((bug) => (
                <BugCard key={bug.id} bug={bug} onUpdate={fetchBugs} />
              ))
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

