const { test, expect } = require('@playwright/test');

test.describe('DevFlow Tracker', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('htt<LOCAL_PATH>');
    await page.waitForLoadState('networkidle');
  });

  test('should display dashboard', async ({ page }) => {
    await expect(page.locator('text=Dashboard')).toBeVisible();
    await expect(page.locator('text=Uebersicht aller Bugs und Ideen')).toBeVisible();
  });

  test('should navigate to bugs page', async ({ page }) => {
    await page.click('text=Bugs');
    await expect(page.locator('text=Neuer Bug')).toBeVisible();
  });

  test('should navigate to ideas page', async ({ page }) => {
    await page.click('text=Ideen');
    await expect(page.locator('text=Neue Idee')).toBeVisible();
  });

  test('should create a new bug', async ({ page }) => {
    await page.click('text=Bugs');
    await page.fill('input[placeholder="Bug beschreiben..."]', 'Test Bug');
    await page.fill('textarea[placeholder="Details eingeben..."]', 'Test Description');
    await page.click('button:has-text("Erstellen")');

    await expect(page.locator('text=Test Bug').first()).toBeVisible();
  });

  test('should create a new idea', async ({ page }) => {
    await page.click('text=Ideen');
    await page.fill('input[placeholder="Idee beschreiben..."]', 'Test Idea');
    await page.fill('textarea[placeholder="Details eingeben..."]', 'Test Description');
    await page.click('button:has-text("Erstellen")');

    await expect(page.locator('text=Test Idea').first()).toBeVisible();
  });

  test('should change bug status', async ({ page }) => {
    await page.click('text=Bugs');
    await page.fill('input[placeholder="Bug beschreiben..."]', 'Status Test Bug');
    await page.click('button:has-text("Erstellen")');

    await page.click('text=Status Test Bug');
    await page.click('button[title="Edit"]');

    const select = page.locator('select').first();
    await select.selectOption('in_progress');

    await expect(page.locator('text=In Bearbeitung').first()).toBeVisible();
  });

  test('should delete a bug', async ({ page }) => {
    await page.click('text=Bugs');
    await page.fill('input[placeholder="Bug beschreiben..."]', 'Delete Test Bug');
    await page.click('button:has-text("Erstellen")');

    await page.hover('text=Delete Test Bug');
    await page.click('button[title="Delete"]');

    await expect(page.locator('text=Delete Test Bug')).not.toBeVisible();
  });
});

