import sqlite3 from 'sqlite3';
import { open } from 'sqlite';
import path from 'path';
import fs from 'fs';

const DB_DIR = path.join(process.cwd(), 'data');
const DB_PATH = path.join(DB_DIR, 'database.sqlite');

let db = null;

export async function getDb() {
  if (db) return db;

  if (!fs.existsSync(DB_DIR)) {
    fs.mkdirSync(DB_DIR, { recursive: true });
  }

  db = await open({
    filename: DB_PATH,
    driver: sqlite3.Database
  });

  await db.run('PRAGMA journal_mode = WAL');
  await initSchema(db);
  return db;
}

async function initSchema(db) {
  await db.exec(`
    CREATE TABLE IF NOT EXISTS bugs (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      nr TEXT NOT NULL UNIQUE,
      name TEXT NOT NULL,
      description TEXT,
      status TEXT NOT NULL DEFAULT 'open',
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
    );

    CREATE TABLE IF NOT EXISTS ideas (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      nr TEXT NOT NULL UNIQUE,
      name TEXT NOT NULL,
      description TEXT,
      status TEXT NOT NULL DEFAULT 'new',
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
    );

    CREATE INDEX IF NOT EXISTS idx_bugs_status ON bugs(status);
    CREATE INDEX IF NOT EXISTS idx_bugs_created_at ON bugs(created_at);
    CREATE INDEX IF NOT EXISTS idx_ideas_status ON ideas(status);
    CREATE INDEX IF NOT EXISTS idx_ideas_created_at ON ideas(created_at);
  `);
}

export async function getNextNumber(type) {
  const db = await getDb();
  const prefix = type === 'bug' ? 'BUG' : 'IDEA';

  const result = await db.get(
    `SELECT nr FROM ${type}s WHERE nr LIKE ? ORDER BY id DESC LIMIT 1`,
    [`${prefix}-%`]
  );

  let nextNum = 1;
  if (result && result.nr) {
    const match = result.nr.match(/\d+/);
    if (match) {
      nextNum = parseInt(match[0]) + 1;
    }
  }

  return `${prefix}-${String(nextNum).padStart(3, '0')}`;
}

export async function getAllBugs() {
  const db = await getDb();
  return db.all('SELECT * FROM bugs ORDER BY created_at DESC');
}

export async function getBugById(id) {
  const db = await getDb();
  return db.get('SELECT * FROM bugs WHERE id = ?', id);
}

export async function createBug({ name, description, status = 'open' }) {
  const db = await getDb();
  const nr = await getNextNumber('bug');

  const result = await db.run(
    `INSERT INTO bugs (nr, name, description, status) VALUES (?, ?, ?, ?)`,
    [nr, name, description, status]
  );

  return getBugById(result.lastID);
}

export async function updateBug(id, updates) {
  const db = await getDb();
  const allowedFields = ['name', 'description', 'status'];
  const fields = [];
